import org.joget.apps.app.service.AppUtil;
import org.joget.commons.util.LogUtil;

if(ticket == null || ticket.length == 0){
    return "No Ticket ID";
}

String ticketId = ticket[0];

if(ticketId.isEmpty()){
    return "No Ticket ID";
}

//LogUtil.info("datalistHashTicketAttachment", "Getting Ticket Attachment for " + ticketId);

String valueUser = FormUtil.getValue("user_colaborador");

String result = "$$user." + valueUser + ".department.name$$";
result = result.replaceAll("\\$\\$", "#");
result = AppUtil.processHashVariable(result, null, null, null);

return result;
