String ProcessID = workflowAssignment.getProcessId();

//Set the workflow variable
WorkflowManager wm = (WorkflowManager) pluginManager.getBean("workflowManager");
wm.processVariable(ProcessID, "flag_ultima_atividade" , "0");
wm.processVariable(ProcessID, "proxima_operativa" , "");


public String calculateSLAD(String dataInicio, Collection feriados, String sla, String urgencia){
 
 DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm");
 DateTimeFormatter dateFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd");
 LocalDateTime dateInit = LocalDateTime.parse(dataInicio, formatter); 
 LocalDateTime endDateTime = null;
 String endDate = "";
 Collection holidays = feriados;
 
 
 var hour = dateInit.getHour();
 int slaNumber = Integer.parseInt(sla);
 
 if(urgencia.equals("SIM")){
 //Calcular SLA urgente
 endDateTime = dateInit.plusHours(slaNumber);
 //Passar dias não uteis
 while(isWeekend(endDateTime) || holidays.contains(endDateTime.format(dateFormatter))){
 endDateTime = endDateTime.plusDays(1);
 }
 
 endDate = endDateTime.format(formatter);
 
 
 
 }else{
 //Calcular SLA não urgente

 if(hour < 12){
 endDateTime = dateInit.withHour(23).withMinute(59); /** HORAS UTEIS??? **/
 
 //Incrementar dias consoante o SLA
 while(slaNumber > 1){
 endDateTime = endDateTime.plusDays(1);
 //Passar dias não uteis
 while(isWeekend(endDateTime) || holidays.contains(endDateTime.format(dateFormatter))){
 endDateTime = endDateTime.plusDays(1);
 } 
 slaNumber -= 1;
 }
 }else{
 endDateTime = dateInit.plusDays(1).withHour(12).withMinute(0);
 
 //Incrementar dias consoante o SLA
 while(slaNumber > 1){
 endDateTime = endDateTime.plusDays(1);
 //Passar dias não uteis
 while(isWeekend(endDateTime) || holidays.contains(endDateTime.format(dateFormatter))){
 endDateTime = endDateTime.plusDays(1);
 }
 slaNumber -= 1;
 }
 }
 
 endDate = endDateTime.format(formatter);
 }
 
 return endDate;
}


public boolean isWeekend(LocalDateTime dt) {
 String dayOfWeek = dt.getDayOfWeek().toString();
 //LogUtil.info("DAY OF WEEK", "" + dayOfWeek);
 switch(dayOfWeek) {
 case "SATURDAY":{
 return true;
 }
 case "SUNDAY":{
 return true;
 }
 default:{
 return false;
 }
 }
}



public String calculateSLADuration(String dataInit){
 DateTimeFormatter formatter = DateTimeFormatter.ofPattern("dd/MM/yyyy HH:mm");
 String result="";
 //if Data Init date format is not "dd/MM/yyyy HH:mm"
 if(dataInit.charAt(2) != '/'){
 //if Data Init date format is of the format "yyyy-MM-dd HH:mm"
 if(dataInit.charAt(4) == '-' && dataInit.charAt(10) == ' ' && dataInit.charAt(13) == ':'){
 dataInit = dataInit.substring(8,10) + "/" + dataInit.substring(5,7) + "/" + dataInit.substring(0,4) + " " + dataInit.substring(11,16);
 }
 }

 LocalDateTime dateInit = LocalDateTime.parse(dataInit, formatter);
 LocalDateTime dateNow = LocalDateTime.parse("#date.dd/MM/yyyy HH:mm#",formatter);
 
 long days = ChronoUnit.DAYS.between(dateInit, dateNow);
 int initHours = dateInit.getHour();
 int nowHours = dateNow.getHour();
 
 
 if(initHours > 12 && nowHours <= 12){
 days-=1; 
 }
 
 result ="D";
 if(days>0){
 result += "+"+days;
 }
 
 
 
 return result;
}



public void updateFaseProcesso(){
 Connection con = null;
 PreparedStatement stmt = null;
 ResultSet rs = null;
 
 try{
 DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();
 
 if(!con.isClosed()){
 String operativa_anterior = "#variable.operativa_anterior#";
 String id_processo_anterior = "#variable.id_processo_anterior#";
 
 if(!operativa_anterior.equals("FILTRO") && !operativa_anterior.trim().isEmpty()){
 //Obter data Inicial do SLA
 String queryGetDateInitSla = "SELECT c_data_inicio FROM app_fd_sla_atividade WHERE c_id_processo=? AND c_estado_atividade = 'Em curso';";
 stmt = con.prepareStatement(queryGetDateInitSla);
 stmt.setObject(1, id_processo_anterior);
 rs = stmt.executeQuery();
 String dataInit ="";
 if(rs.next()){
 dataInit = rs.getObject("c_data_inicio").toString();
 }
 rs=null;
 //LogUtil.info("AAAAAAAA", "ID PROCESSO ANTERIOR" + id_processo_anterior);
 //LogUtil.info("AAAAAAAA", "DATA INIT" + dataInit);
 
 String slaDuration = calculateSLADuration(dataInit);
 //Atualizar o sla Em curso para fechado
 String queryReset= "UPDATE app_fd_sla_atividade SET c_estado_atividade = ?, c_data_conclusao=? , c_duracao = ? WHERE c_id_processo=? AND c_estado_atividade = 'Em curso';"; 
 stmt = con.prepareStatement(queryReset);
 if(operativa_anterior.equals("CONTRATACAO")){
 stmt.setObject(1,"Em espera");
 }else{
 stmt.setObject(1,"Fechada");
 }
 stmt.setObject(2,"#date.yyyy-MM-dd HH:mm#");
 stmt.setObject(3, slaDuration);
 stmt.setObject(4, id_processo_anterior);
 rs = stmt.executeQuery();
 rs=null;
 
 //Obter detalhes da tarefa
 String queryGetAssignment = "SELECT name FROM shkprocesses WHERE id=?;";
 String queryGetUserAssign = "SELECT resourceid FROM shkassignmentstable WHERE activityprocessid =?";
 String nomeAtividade = "";
 String utilizador = "";
 stmt = con.prepareStatement(queryGetAssignment);
 stmt.setObject(1, ProcessID);
 rs = stmt.executeQuery();
 
 if(rs.next()){
 nomeAtividade= rs.getObject("name").toString();
 rs=null;
 }
 
 stmt = con.prepareStatement(queryGetUserAssign);
 stmt.setObject(1, ProcessID);
 rs = stmt.executeQuery();
 
 if(rs.next()){
 utilizador= rs.getObject("resourceid").toString();
 rs=null;
 }
 
 //Obter feriados
 String queryGetHolidays = "SELECT * FROM app_fd_lista_feriados;";
 stmt = con.prepareStatement(queryGetHolidays);
 rs = stmt.executeQuery();
 Collection holidays = new ArrayList(); 
 
 while(rs.next()){
 holidays.add(rs.getObject("c_data"));
 }
 
 //Obter Urgencia
 String urgente = "";
 String recordId = "#process.recordId#";
 String queryGetUrgencia = "SELECT c_urgencia FROM app_fd_detalhe_processo WHERE c_id_processo = ?";
 stmt = con.prepareStatement(queryGetUrgencia);
 stmt.setObject(1, recordId);
 rs = stmt.executeQuery();
 
 if(rs.next()){
 urgente = rs.getObject("c_urgencia");
 }
 
 //Obter configurações de SLA consoante a operativa e urgencia
 String paramOperativa = "FILTRO";
 String queryGetSLAConfig = "SELECT * FROM app_fd_valores_sla WHERE c_nome like ? ";
 if(urgente.equals("SIM")){
 queryGetSLAConfig += "AND c_nome like '%URGENTE'";
 paramOperativa += "%";
 }
 
 stmt = con.prepareStatement(queryGetSLAConfig);
 stmt.setObject(1, paramOperativa);
 rs = stmt.executeQuery();
 String sla = ""; 
 if(rs.next()){
 sla = rs.getObject("c_sla");
 }
 
 
 String queryCreateSLA = "INSERT INTO app_fd_sla_atividade (id, dateCreated, dateModified, createdBy,createdByName ,modifiedBy, modifiedByName, c_data_inicio, c_data_fim, c_nome_atividade, c_id_processo, c_utilizador_atribuido, c_estado_atividade, c_duracao) VALUES (?,?,?,?,?,?,?,?,?,?,?,?,?,?);";
 stmt = con.prepareStatement(queryCreateSLA);
 
 var idSLA = UuidGenerator.getInstance().getUuid();
 var dateNow ="#date.yyyy-MM-dd HH:mm#";
 var currentUsername = "#currentUser.username#";
 var currentNameUser = "#currentUser.firstName#" + "#currentUser.lastName#";
 String dataFim = calculateSLAD(dateNow, holidays, sla, urgente);
 String duracao = "D";
 //LogUtil.info("AAAAAAAA", "DATA FIM" + dataFim);
 var estadoAtividade = "Em curso";
 stmt.setObject(1, idSLA);
 stmt.setObject(2, dateNow);
 stmt.setObject(3, dateNow);
 stmt.setObject(4, currentUsername);
 stmt.setObject(5, currentNameUser);
 stmt.setObject(6, currentUsername);
 stmt.setObject(7, currentNameUser);
 stmt.setObject(8, dateNow);
 stmt.setObject(9, dataFim); 
 stmt.setObject(10, nomeAtividade);
 stmt.setObject(11, ProcessID);
 stmt.setObject(12, utilizador);
 stmt.setObject(13, estadoAtividade);
 stmt.setObject(14, duracao);
 
 rs = stmt.executeQuery();
 
 }
 //Atualizar fase do processo
 String recordId = "#process.recordId#";
 if(recordId!=null){
 String updateFaseProcesso = "UPDATE app_fd_detalhe_processo SET c_fase_processo = ? WHERE c_id_processo = ?";
 stmt = con.prepareStatement(updateFaseProcesso);
 stmt.setObject(1, "FLT");
 stmt.setObject(2, recordId);
 rs = stmt.executeQuery();
 }
 }
 
 }catch(Exception e){
 LogUtil.error(appDef.toString(), e, "Error getting info from DB");
 }finally{
 try{
 if(rs != null){
 rs.close();
 }
 if(stmt != null){
 stmt.close();
 }
 if(con != null && !con.isClosed()) {
 con.close();
 }
 }catch(Exception e){
 LogUtil.error(appDef.toString(), e, "Error closing DB connection");
 }
 
 }
}
updateFaseProcesso();
wm.processVariable(ProcessID, "operativa_anterior" , "FILTRO");
wm.processVariable(ProcessID, "id_processo_anterior" , ProcessID);
"}

