public void pollEmail() {
 LogUtil.info("Email ->","Plugin");

 Properties properties = new Properties();
 if ("true".equals("#form.email_listener.debug_imap?java#")) {
 properties.put("mail.debug", "true");
 }
 properties.put("mail.store.protocol", "imaps");
 properties.put("mail.imaps.host", "#form.email_listener.host?java#");
 properties.put("mail.imaps.port", "#form.email_listener.port?java#");
 properties.put("mail.imaps.timeout", (#form.email_listener.timeout?java# * 1000));



 Session session = Session.getInstance(properties);


 IMAPStore store = null;
 Folder inbox = null;

 int messageCount = 0;
 String errors = "";
 try {
 debug("Connect to IMAP for #form.email_listener.email?java#");
 //debug( "Password #form.email_listener.password?java#");
 store = (IMAPStore) session.getStore("imaps");
 store.connect("#form.email_listener.email?java#", "#form.email_listener.password?java#");
 debug("IMAP connected for #form.email_listener.email?java#");

 inbox = (IMAPFolder) store.getFolder("#form.email_listener.folder?java#");
 if (null == folder) {
 throw new Exception(" no folder [#form.email_listener.folder?java#] for user [#form.email_listener.email?java#] on host [#form.email_listener.host?java#]");
 }

 // opening found folder in read/write
 inbox.open(Folder.READ_WRITE);

 messageCount = inbox.getUnreadMessageCount();
 debug("Unread Messages: " + messageCount);
 debug("Filter email by exact subject text: #form.email_listener.subject_filter?java#");
 debug("Filter email by regex subject text: #form.email_listener.subject_pattern?java#");

 int count = 0;

 Message[] messages = inbox.search(new FlagTerm(new Flags(Flags.Flag.SEEN), false));

 for (Message message : messages) {
 try {
 if (count > #form.email_listener.max_email?java#) {
 debug("Email checking reach max email per check(#form.email_listener.max_email?java#).");
 break;
 }


 // get email details
 String subject = message.getSubject();
 if(!matchFilter(subject)) {
 continue;
 }

 InternetAddress senderIA = (InternetAddress) message.getFrom()[0];
 String sender = senderIA.getAddress();

 String content = "";
 if (message.getContent() instanceof Multipart) {

 Multipart multipart = (Multipart) message.getContent();
 debug("MULTIPART " + multipart);
 content = getTextFromMimeMultipart(multipart);

 } else {
 debug("NO MULTIPART" + "BBB");
 content = message.getContent().toString();
 }


 storeEmail(message, subject, content, sender);


 count++;
 } catch (MessagingException e) {
 errors += e.getMessage() + "\n";
 LogUtil.error("App: eas - Poll Email tool", e, e.getMessage());
 }
 }

 } catch (Exception e) {
 errors += e.getMessage() + "\n";
 LogUtil.error("App: eas - Poll Email tool", e, "");
 } finally {
 try {
 if (inbox != null && inbox.isOpen()) {
 inbox.close(false);
 }
 } catch (Exception e) {
 // ignore
 }

 try {
 if (store != null && store.isConnected()) {
 store.close();
 }
 } catch (Exception e) {
 // ignore
 }
 }
 if (!errors.isEmpty()) {
 addError(messageCount, errors);
 }
 storeFormData("j_ave_log", "log", activitiesLog);

}



public void storeEmail(Message msg, String sbj, String content, String sender){

 Pattern patternGetNWfPl = Pattern.compile("(\\w{3,}\\d{6,}|\\d{6,})");
 Matcher m = patternGetNWfPl.matcher(sbj);
 m.find();
 String nWfPl = m.group(1);

 //LogUtil.info("Numero Wf Pl", "aa" + nWfPl);

 Connection con = null;
 PreparedStatement stmt = null;
 ResultSet rs = null;
 UuidGenerator uuid = UuidGenerator.getInstance();
 String pId = "";
 String nomeFicheiro = "EMAIL-" + sbj + ".eml";
 nomeFicheiro = nomeFicheiro.replaceAll("[^a-zA-Z0-9\\.\\-]", "_");

 try{



 DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();

 if(!con.isClosed()){

 String queryGetProcessId = "SELECT c_id_processo, c_estado_contratacao FROM app_fd_detalhe_processo WHERE c_n_wf_pl = ? OR c_n_wf_banco = ?";
 stmt = con.prepareStatement(queryGetProcessId);
 stmt.setString(1, nWfPl);
 stmt.setString(2, nWfPl);
 rs = stmt.executeQuery();
 String estadoContratacao="";
 // LogUtil.info("Número WF PL","AAAAAAAAAAA" + nWfPl);
 if(rs.next()){
 estadoContratacao = rs.getObject("c_estado_contratacao").toString();
 // LogUtil.info("Estado Contratação","AAAAAAAAAAA" + estadoContratacao);
 //Inserir Email no processo
 String processId = rs.getObject("c_id_processo").toString();
 String queryInsertEmail = "INSERT INTO app_fd_comunicacoes_proce(id,c_assunto, c_destinatarios,c_mensagem, c_id_processo, c_anexos) VALUES(?,?,?,?,?,?)"; 
 pId=uuid.getUuid();


 stmt = con.prepareStatement(queryInsertEmail);
 stmt.setString(1, pId);
 stmt.setString(2,sbj);
 stmt.setString(3,sender);
 stmt.setString(4, content);
 stmt.setString(5, processId);
 stmt.setString(6, nomeFicheiro);
 stmt.executeQuery();
 LogUtil.info("Email Pool " - "Foi guardado um email no processo " + processId);
 //Caso o estado estja pendente
 if(estadoContratacao.equals("PND") || estadoContratacao.equals("PNDEQ")){
 //Atualizar Estado do processo 
 String queryUpdateState = "UPDATE app_fd_detalhe_processo SET c_estado_contratacao = 'ATV' WHERE c_n_wf_pl = ?";
 stmt = con.prepareStatement(queryUpdateState); 
 stmt.setString(1, nWfPl);
 stmt.executeQuery();

 String subProcessId= "";
 String activityId = "";
 //Obter Id do subProcesso
 String queryGetProcessId = "SELECT Id FROM shkprocesses WHERE ActivityRequesterProcessId = ? order by created desc ;";
 stmt = con.prepareStatement(queryGetProcessId);
 stmt.setObject(1, processId);
 rs = stmt.executeQuery();
 if(rs.next()){
 subProcessId = rs.getObject("Id");

 //Atualizar o sla de Pendente para Em curso
 String queryReset= "UPDATE app_fd_sla_atividade SET c_estado_atividade = 'Em curso' WHERE c_id_processo=? AND c_estado_atividade = 'Pendente';"; 
 stmt = con.prepareStatement(queryReset);
 stmt.setObject(1, subProcessId);
 rs = stmt.executeQuery();
 LogUtil.info("Email Pool " - "Foi atualizado o sla do processo " + processId);
 String queryGetActivityId = "SELECT id FROM shkactivities WHERE processId= ? order by oid desc";
 stmt = con.prepareStatement(queryGetActivityId);
 stmt.setObject(1, subProcessId);
 rs = stmt.executeQuery();
 if(rs.next()){
 activityId = rs.getObject("id").toString();
 //Atualizar Workflow varible 
 WorkflowManager wm = (WorkflowManager) pluginManager.getBean("workflowManager");
