 String alertaSwift = "";
 String userValidacao = "";
 Connection con2 = null;
 PreparedStatement stmt2 = null;
 PreparedStatement stmt3 = null;
 ResultSet rs2 = null;
 ResultSet rs3 = null;

 try{
 DataSource ds = (DataSource) AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();

 if(!con.isClosed()){


 String query = "SELECT p.id as 'IDProcesso', a.id as 'IDAgendamento', b.c_nome_banco, dp.c_complex_processo ,sass.resourceid as 'ResourceID', a.c_flag_alerta_agendamento as 'Alerta', dp.c_fase_processo as 'FaseProcesso', c_n_wf_pl as 'NWFPL', c_flag_alerta_agendamento_swift as 'AlertaSwift', sactdata.VariableValueVCHAR as 'UserValidacao' FROM app_fd_agendamento a JOIN app_fd_processos p ON p.c_agendamento = a.id LEFT JOIN app_fd_banco b ON b.id = p.c_banco LEFT JOIN app_fd_detalhe_processo dp ON dp.id = p.c_detalhe_processo JOIN shkprocesses shk ON p.id = shk.ActivityRequesterProcessId JOIN SHKActivities sact ON sact.ProcessId = shk.Id JOIN shkactivitydata sactdata ON sact.oid = sactdata.activity JOIN SHKAssignmentsTable sass ON sact.Id = sass.ActivityId WHERE a.c_data_agendamento = DATE_SUB(CURDATE(), INTERVAL -1 DAY) AND sactdata.VariableDefinitionId = 'user_validacao';";
 stmt = con.prepareStatement(query);

 rs = stmt.executeQuery();

 while(rs.next()){
 //Alertas Pedidos de Guia
 processId = rs.getString(1);
 resourceId = rs.getString(5);
 alerta = rs.getObject("Alerta");
 idAgendamento = rs.getObject("IDAgendamento");
 faseProcesso= rs.getObject("FaseProcesso");
 nWfPl= rs.getObject("NWFPL");
 alertaSwift = rs.getObject("AlertaSwift");
 userValidacao= rs.getObject("UserValidacao");

 //Se o não estiver já com alerta
 if(!alerta.equals("SIM") && !userValidacao.isEmpty() ){
 //LogUtil.info("ALERTA", "AAAAA");
 String banco =rs.getString(3);
 String complexidade = rs.getString(4);
 String equipaDest = "SISTEMA";
 String equipaRem = "PL_INT_VAL_" + banco + "_" + complexidade + "_CRH";

 Map variables = new HashMap();
 variables.put("equipa_destinataria", equipaDest);
 variables.put("gestor_remetente", userValidacao);
 variables.put("gestor_destinatario", "SISTEMA");
 variables.put("equipa_remetente",equipaRem );
 variables.put("flag_alerta","1" );

 WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "Diligências");

 WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);

 diligenciaId = result.getProcess().getInstanceId();

 try{
 con2 = ds.getConnection();

 if(!con2.isClosed()){
 String activityIdQuery = "select ass.activityID, ass.ResourceID from shkprocesses pro left join shkassignmentstable ass on pro.id = ass.ActivityProcessID where pro.id = ?;";
 stmt2 = con2.prepareStatement(activityIdQuery);
 stmt2.setObject(1, diligenciaId);
 rs2 = stmt2.executeQuery();

 if(rs2.next()){
 workflowManager.assignmentForceComplete(process.getId(), diligenciaId, rs2.getString(1), rs2.getString(2));

 //LogUtil.info("ID Diligencia -> ", diligenciaId);
 String assunto = "Alerta | Data de Agendamento | Processo - " + nWfPl;
 String mensagem = "Data de agendamento do processo " + nWfPl + " dentro de 24 horas. Pedir guias de imposto.";
 String insertQuery = "INSERT INTO app_fd_info_diligencia(id, c_id_processo, c_operativa_destinataria, c_equipa_destinataria, c_gestor_destinatario, c_operativa_remetente, c_equipa_remetente, c_gestor_remetente, c_assunto, c_tipo_diligencia, c_diligencia, c_urgencia, c_banco, c_complexidade, dateCreated) VALUES(?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";
 stmt3 = con2.prepareStatement(insertQuery);
 stmt3.setObject(1, diligenciaId);
 stmt3.setObject(2, processId);
 stmt3.setObject(3, faseProcesso);
 stmt3.setObject(4, equipaDest);
 stmt3.setObject(5, "SISTEMA");
 stmt3.setObject(6, faseProcesso);
 stmt3.setObject(7, equipaRem);
 stmt3.setObject(8, userValidacao);
 stmt3.setObject(9, assunto);
 stmt3.setObject(10, "ALERTA");
 stmt3.setObject(11, mensagem);
 stmt3.setObject(12, "SIM");
 stmt3.setObject(13, banco);
 stmt3.setObject(14, complexidade);
 stmt3.setObject(15, "#date.yyyy-MM-dd hh:mm:ss#");
 stmt3.executeQuery();
 LogUtil.info("Alertas Validação ", "Foi enviada uma diligência automática relativa ao processo " + processId);
 }

 //Atualizar flag de alerta para SIM
 String queryUpdateAgendamentoFlag = "UPDATE app_fd_agendamento SET c_flag_alerta_agendamento = 'SIM' WHERE id = ?";
 stmt2 = con2.prepareStatement(queryUpdateAgendamentoFlag);
 stmt2.setObject(1, idAgendamento);
 rs2 = stmt2.executeQuery();



 }
 } catch(Exception e) {
 LogUtil.error("Sample app - Form 1", e, "Error loading user data in load binder");
 } finally{
 try{
 if(rs2 != null){
 rs2.close();
 }
 if(rs3 != null){
 rs3.close();
 }
 if(stmt2 != null){
 stmt2.close();
 }
 if(stmt3 != null){
 stmt3.close();
 }
 if(con2 != null && !con2.isClosed()) {
 con2.close();
 }
 }catch(Exception e){
 LogUtil.error(appDef.toString(), e, "Error closing DB connection");
 }
 }
 }

 //Alerta Swift
 if(!alertaSwift.equals("SIM")){
 String banco =rs.getString(3);
 String complexidade = rs.getString(4);
 String equipaDest = "SISTEMA";
 String equipaRem = "PL_INT_"+faseProcesso+"_" + banco + "_" + complexidade + "_CRH";

 Map variables = new HashMap();
 variables.put("equipa_destinataria", equipaDest);
 variables.put("gestor_remetente", resourceId);
 variables.put("gestor_destinatario", "SISTEMA");
 variables.put("equipa_remetente",equipaRem );
 variables.put("flag_alerta","1" );

