{"script":"import org.joget.workflow.model.service.*;
import org.joget.commons.util.LogUtil;
import org.joget.apps.app.service.AppUtil;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import javax.sql.DataSource;

String ProcessID = workflowAssignment.getProcessId();

//Set the workflow variable
WorkflowManager wm = (WorkflowManager) pluginManager.getBean("workflowManager");
wm.processVariable(ProcessID, "estado" , "FLT");

public void updateEstado(){
 Connection con = null;
 PreparedStatement stmt = null;
 ResultSet rs = null;
 
 try{
 DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();
 
 if(!con.isClosed()){
 
 //Atualizar fase do processo
 String recordId = "#process.recordId#";
 if(recordId!=null){
 String updateFaseProcesso = "UPDATE app_fd_detalhe_processo SET c_estado_contratacao = ? WHERE c_id_processo = ?";
 stmt = con.prepareStatement(updateFaseProcesso);
 stmt.setObject(1, "FLT");
 stmt.setObject(2, recordId);
 rs = stmt.executeQuery();
 LogUtil.info("Fluxo Filtro Beanshell Tool", "Estado do processo " + recordId + " atualizado para filtrado");
 }
 }
 
 }catch(Exception e){
 LogUtil.error(appDef.toString(), e, "Error getting info from DB");
 }finally{
 try{
 if(rs != null){
 rs.close();
 }
 if(stmt != null){
 stmt.close();
 }
 if(con != null && !con.isClosed()) {
 con.close();
 }
 }catch(Exception e){
 LogUtil.error(appDef.toString(), e, "Error closing DB connection");
 }
 
 }
}
updateEstado();
"}

