import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.dao.FormDataDao;
import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.form.model.FormRow;
import org.joget.commons.util.LogUtil;
import org.joget.apps.form.service.FormUtil;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;

public FormRowSet load(String[] values) {
    
    FormRowSet rows = new FormRowSet();
    String servico = "#form.processos_hip.servico#";
    //set id_processo based on dependency value
    String id_processo = "";
   
    if (values != null && values.length > 0) {
        id_processo = values[0];
        //LogUtil.info("Beanshell document - imovel", "id_processo: " + id_processo);
        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
        try{
            //conect to DB
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            String query = "";
            if(!con.isClosed()){
                //get all fracoes of all the imoveis related to the received id_processo
                if(servico.equals("HIP")){
                    query = "Select listProcess from (select c_tipo_processo_1 as listProcess, c_id_processo from app_fd_detalhe_proce_hip in1 union all select c_tipo_processo_2 as listProcess, c_id_processo from app_fd_detalhe_proce_hip in2 union all select c_tipo_processo_3 as listProcess, c_id_processo from app_fd_detalhe_proce_hip in3 union all select c_tipo_processo_4 as listProcess, c_id_processo from app_fd_detalhe_proce_hip in4 union all select c_tipo_processo_5 as listProcess, c_id_processo from app_fd_detalhe_proce_hip in5) total where c_id_processo = ?";
                }else if(servico.equals("LEA")){
                    query ="Select listProcess from (select c_tipo_processo_lea as listProcess, c_id_processo from app_fd_detalhe_proce_hip in1) total where c_id_processo = ?"; 
                }
                stmt = con.prepareStatement(query);
                stmt.setObject(1, id_processo);
                rs = stmt.executeQuery();
                int count = 0;
                if (rs.last()) {
                    count = rs.getRow();
                    rs.beforeFirst(); // not rs.first() because the rs.next() below will move on, missing the first element
                }
                if (count > 2){
                        //LogUtil.info("Beanshell document - imovel ", "Count: " + count);
                        //if we get more than one result from the query, add empty row
                        FormRow option = new FormRow();
                        option.setProperty(FormUtil.PROPERTY_VALUE, "");
                        option.setProperty(FormUtil.PROPERTY_LABEL, "");
                        rows.add(option);
                }
                
                while (rs.next()) {
                    if(rs.getString(1) != null){
                        String opcao = rs.getString(1);
                        FormRow option = new FormRow();
                        option.setProperty(FormUtil.PROPERTY_VALUE, opcao);
                        option.setProperty(FormUtil.PROPERTY_LABEL, opcao);
                        rows.add(option);
                    }
                }
            }
            }catch(Exception e){
                LogUtil.error("Beanshell document - imovel", e, "Error getting info from DB");
            }finally{
                try{
                    if(rs != null){
                    rs.close();
                    }
                    if(stmt != null){
                        stmt.close();
                    }
                    if(con != null && !con.isClosed()) {
                        con.close();
                    }
                }catch(Exception e){
                    LogUtil.error("Beanshell document - imovel", e, "Error closing DB connection");
                }
                
            }
    }
    return rows;
}
 
//call load method with injected variable
return load(values);

