public static String removerAcentos(String str) {
    return Normalizer.normalize(str, Normalizer.Form.NFD).replaceAll("[^\\p{ASCII}]", "");
}

public FormRowSet getFaseProcesso() {

    FormRowSet f = new FormRowSet();

    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    String origemVal = "";
    String origem = "";

    AppDefinition appDef = AppUtil.getCurrentAppDefinition();


    try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();


            if(!con.isClosed()){


                String ProcessID = "#process.processId#";
                String idDetalheProcesso = "#form.detalhe_proce_hip.id[{form.processos_hip.detalhe_processo}]#";


                /**Caso outro utilizador que não o assignado à tarefa faça alterações no processo, não será possivel obter o id do processo**/
                //Assumindo que não existem subprocessos em paralelo
                //Obter o id do processo mais recente
                if(ProcessID.startsWith("#process.processId")){

                    //Obter campos do formulário de detalhe do processo
                    Map paramValues = FormUtil.loadFormData(appDef.getAppId(), appDef.getVersion().toString(), "HIPDetalheProcesso", idDetalheProcesso, false, false, false, null);
                    String mainProcessId = paramValues.get("id_processo");
                    String queryGetProcessId = "SELECT Id FROM shkprocesses WHERE ActivityRequesterProcessId = ? order by created desc ;";
                    stmt = con.prepareStatement(queryGetProcessId);
                    stmt.setObject(1, mainProcessId);
                    rs = stmt.executeQuery();
                    if(rs.next()){
                        ProcessID = rs.getObject("Id");
                    }
                }


                boolean found = false;

                String queryGetFaseLabel = "SELECT c_value, c_label FROM app_fd_listas_de_valores WHERE c_nome_campo = 'fase_processo' AND c_servico LIKE ?";
                stmt = con.prepareStatement(queryGetFaseLabel);
                stmt.setObject(1, "%HIP%");
                rs = stmt.executeQuery();
                while(rs.next() && !found){
                    String fase_value = rs.getString(1);
                    String fase_label = rs.getString(2);
                    if(ProcessID.contains(removerAcentos(fase_label)) ){
                        FormRow r1 = new FormRow();
                        r1.put(FormUtil.PROPERTY_VALUE, fase_value);
                        r1.put(FormUtil.PROPERTY_LABEL, fase_label);
                        f.add(r1);

                        found = true;

                        String queryUpdateFaseProcesso = "UPDATE app_fd_detalhe_proce_hip SET c_fase_processo = ? WHERE id = ?";
                        stmt = con.prepareStatement(queryUpdateFaseProcesso);
                        stmt.setObject(1, fase_value);
                        stmt.setObject(2, idDetalheProcesso); //id registo do detalhe_processo
                        rs = stmt.executeQuery();

                    }

                }

            }

    }catch(Exception e){
        LogUtil.error("HIPOTECAS - Resumo - fase de processo", e, "Error getting info from DB");
    }finally{
        try{
            if(rs != null){
            rs.close();
            }
            if(stmt != null){
                stmt.close();
            }
            if(con != null && !con.isClosed()) {
                con.close();
            }
        }catch(Exception e){
            LogUtil.error("HIPOTECAS - Resumo - fase de processo", e, "Error closing DB connection");
        }

    }

    return f;
}

return getFaseProcesso();

