public FormRowSet storeData(Element element, FormRowSet rows, FormData formData) {
    AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
    WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
    AppDefinition appDef = AppUtil.getCurrentAppDefinition();
        
    try{
        //check for empty data
        if (rows == null || rows.isEmpty()) {
            return rows;
        }
        
        //get value from form to set workflow variables
        FormRow originalRow = rows.get(0);
        
        String bancoValor = originalRow.getProperty("nome_banco");
        String complexidadeValor = "NOR";
        String nWFBanco;
        String comentarios = originalRow.getProperty("comentarios_processo");
        String existeNWF = originalRow.getProperty("existe_n_wf");
        
        String formaOutorga;

        
        if(originalRow.getProperty("forma_outorga") == null){
            formaOutorga = "";
        }else{
            formaOutorga = originalRow.getProperty("forma_outorga");
        }
        
        
        /**switch date format
         * Date format received: 14/10/2022 16:00
         * Date format needed: 2022-10-14 13:00
         * **/
        //data format we receive: 14/10/2022 16:00
        //data format we need: 2022-10-14 13:00
        String receivedDate = originalRow.getProperty("data_entrada");
        String finalDate;
        if (receivedDate != null && receivedDate.length() > 0){ //se data esta preenchida
            finalDate = receivedDate.substring(6,10) + "-" + receivedDate.substring(3,5) + "-" + receivedDate.substring(0,2) + " " + receivedDate.substring(11,16);
        }else{
            finalDate = "";
        }
        
        String n_wf_pl = "";
        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
        boolean existe = false;
        String processId = null;
        
        
        /**create n_wf_pl
         * If the flag to automatically generate the number is active then we need to obtain the maximum number generated untill now and increment that value
         * TODO - TOrnar automatico o mecanismo de de ir buscar o numero maximo existente
         * **/
         
        
        if(existeNWF.equals("NAO")){
            //Get maximum valu from DB
            try{
                DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                con = ds.getConnection();
                
                if(!con.isClosed()){
                    //Set query
                    String query = "SELECT Max(replace(c_n_wf_pl, ?, '')) as num_max FROM app_fd_processos_hip p JOIN app_fd_detalhe_proce_hip where c_n_wf_pl like ?";
                    stmt = con.prepareStatement(query);
                    
                    if(bancoValor.equals("MTG")){
                        n_wf_pl = "MGLEAG";
                    }else{
                        n_wf_pl = bancoValor + "LEAG";
                    }
                    stmt.setObject(1, n_wf_pl);
                    stmt.setObject(2, n_wf_pl + "%");
                    
                    rs = stmt.executeQuery();
                    
                    //Get Value from result set
                    if (rs.next() && rs.getString(1) != null) {
                        nWFBanco = rs.getString(1);
                        //LogUtil.info(appDef.toString(), "Número de WF  1 : " + nWFBanco);
                    }else{
                        nWFBanco = "0";
                    }
                }
            }catch(Exception e){
                LogUtil.error("LEASING - CRIAR PROCESSO", e, "Error getting info from DB");
            }finally{
                try{
                    if(rs != null){
                        rs.close();
                    }
                    if(stmt != null){
                        stmt.close();
                    }
                    if(con != null && !con.isClosed()) {
                        con.close();
                    }
                }catch(Exception e){
                    LogUtil.error("LEASING - CRIAR PROCESSO", e, "Error closing DB connection");
                }
                
            }
           // LogUtil.info(appDef.toString(), "Número de WF gerado: " + nWFBanco);
            
            //create final number of WF
            nWFBanco = String.valueOf(Integer.valueOf(nWFBanco) + 1);
            nWFBanco = "000000000" + nWFBanco;
            nWFBanco = nWFBanco.substring(nWFBanco.length() - 10);
            
            n_wf_pl += nWFBanco;
            
           // LogUtil.info(appDef.toString(), "Gerar número de processo automaticamente. Número de WF gerado: " + n_wf_pl);
            
        }else{
            nWFBanco = originalRow.getProperty("n_wf_banco");
            if(bancoValor.equals("MTG")){
                n_wf_pl = "MGLEA" + nWFBanco;
            }else{
                n_wf_pl = bancoValor + "LEA" + nWFBanco;
            }
            
            /**Check if the n_wf_pl already exists in the DB
             * if exists boolean existe = true
             * **/
            
            try{
                DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                con = ds.getConnection();
                
                if(!con.isClosed()){
                    String query = "SELECT p.id FROM app_fd_processos_hip p JOIN app_fd_detalhe_proce_hip dp ON dp.id = p.c_detalhe_processo WHERE dp.c_n_wf_pl = ?";
                    stmt = con.prepareStatement(query);
                    stmt.setObject(1, n_wf_pl);
                    rs = stmt.executeQuery();
                    if (rs.next()) {
                        processId = rs.getString(1);
                        String queryGetProcess = "select subpro.id, subpro.PDefName, ass.activityID, ass.ResourceID  from shkprocesses pro left join shkprocesses subpro on pro.id = subpro.activityRequesterProcessID left join shkassignmentstable ass on subpro.id = ass.ActivityProcessID where pro.id = ? and activityID is not null";
                        stmt = con.prepareStatement(queryGetProcess);
                        stmt.setObject(1, processId);
                        rs = stmt.executeQuery();
                        if(rs.next()){
                           existe = true;
                        } 
                        
                    }
                }
            }catch(Exception e){
                LogUtil.error("LEASING - CRIAR PROCESSO", e, "Error getting info from DB when trying to get a process by n_wf_pl");
            }finally{
                try{
                    if(rs != null){
                    rs.close();
                    }
                    if(stmt != null){
                        stmt.close();
                    }
                    if(con != null && !con.isClosed()) {
                        con.close();
                    }
                }catch(Exception e){
                    LogUtil.error("LEASING - CRIAR PROCESSO", e, "Error closing DB connection");
                }
                
            }
        }
            
        
        /** Create new process 
         * estado_contratacao = "NOV" by default
         * put the values of the workflow variables needed into "variables"
         * if the process is starting manually it is allocated to the current user
         * start a new process of type "Hipotecas" with "variables"
         * */
        if(!existe){
            String estado = "";
            if(originalRow.getProperty("estado_contratacao") == null || originalRow.getProperty("estado_contratacao").isEmpty()){
                estado = "NOV";
            }else{
                estado = originalRow.getProperty("estado_contratacao");
            }
            
            //start process with variables
            Map variables = new HashMap();
            variables.put("banco", bancoValor);
            variables.put("estado", estado);
            variables.put("user_contratacao", "#currentUser.username#");
            //variables.put("complexidade", complexidadeValor);
            
            
            //get process
            WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "hipotecas");
            //start process
            WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);
            //get process id
            processId = result.getProcess().getInstanceId();

            
            /** Store fields in DB
             * Generate UUID for each column of app_fd_processos corresponding to a table (fieldIds)
             * Create an entry in every table with the formId in (formIds) with the value of the column id_processo and store other respective fields if any
             * Create an interveniente if either the nif or nome is filled
             * Create an entry in app_fd_processos with all the UUID foerign keys generated
             * */
            
            FormRowSet multipagedForms = new FormRowSet();
            FormRow formPrimaryKey = new FormRow();
            
            //definition of the fields of table processos where to store each generated primary key
            String[] fieldIds = {"detalhe_processo", "intervenientes", "docs", "imoveis", "banco", "anal_banc", "anal_legal", "esclarecimentos", "comunicacoes", "geral", "agendamento"};
            //definition of the formIds that correspond to the pages of the multipage form
            String[] formIds = {"HIPDetalheProcesso", "HIPIntervenientes", "HIPdocumentos", "HIPImoveis", "HIPBanco", "HIPAnaliseBancaria", "HIPAnaliseLegal", "esclarecimentos", "comunicacoes", "HIPGeral", "agendamento"};
            
            //Iterate through the different forms where we need to store each field
            for(int i = 0; i < fieldIds.length; i++){
                String foreignKey = UuidGenerator.getInstance().getUuid();
                formPrimaryKey.setProperty(fieldIds[i], foreignKey);
    
                FormRowSet newRows = new FormRowSet();
                FormRow newRow = new FormRow();
                if(fieldIds[i].equals("detalhe_processo") ){
                    newRow.put("complex_processo", complexidadeValor);
                    newRow.put("fase_processo", "CNT");
                    newRow.put("nome_banco", bancoValor);
                    newRow.put("data_primeira_entrada", finalDate);
                    newRow.put("forma_outorga", formaOutorga);
                    newRow.put("estado_contratacao", estado);
                    newRow.put("criado_por", "#currentUser.username#");
                    newRow.put("n_wf_pl", n_wf_pl);
                    newRow.put("comentarios_processo",comentarios);
                    
                    //if it's a process with a bank associated
                    //if(!n_wf_pl.contains("PLDPA")){
                    newRow.put("n_wf_banco", nWFBanco);
                    //}
                    
                }else if(fieldIds[i].equals("banco") ){
                    newRow.put("nome_banco", bancoValor);
                    
                }else if(fieldIds[i].equals("intervenientes") ){
                    String intervenienteId = UuidGenerator.getInstance().getUuid();
                    if(!originalRow.getProperty("nome").isEmpty() || !originalRow.getProperty("nif").isEmpty()){
                        FormRowSet newGridRows = new FormRowSet();
                        FormRow newGridRow = new FormRow();
                        
                        newGridRow.put("id_intervenientes", foreignKey);
                        newGridRow.put("nome", originalRow.getProperty("nome"));
                        newGridRow.put("nif", originalRow.getProperty("nif"));
                        newGridRow.put("id_processo", processId);
                        newGridRow.put("cliente_principal", "CHECK");
                        newGridRows.add(newGridRow);
                        storeToOtherFormDataTable(appDef, element, newGridRows, formData, "HIPInterveniente", intervenienteId);
                    }
                }
                newRow.setProperty("id_processo", processId);
                newRows.add(newRow);
                storeToOtherFormDataTable(appDef, element, newRows, formData, formIds[i], foreignKey);
            }
            formPrimaryKey.setProperty("servico", "LEA");
            multipagedForms.add(formPrimaryKey);
            
            storeToOtherFormDataTable(appDef, element, multipagedForms, formData, "HIPProcesso", processId);
            
            //LogUtil.info(appDef.toString(), "Novo Processo Criado com sucesso. Número de WF PL gerado: " + n_wf_pl);
             
            /** TODO SLA */
                
        }else{ /**if the process already exists**/
            
            //LogUtil.info(appDef.toString(), "Número de Workflow já existente em sistema");
            FormRowSet newRows = new FormRowSet();
            FormRow newRow = new FormRow();
            
            String estado = "";
            if(originalRow.getProperty("estado_contratacao") == null || originalRow.getProperty("estado_contratacao").length() <= 0){
                estado = "DEF";
            }else{
                estado = originalRow.getProperty("estado_contratacao");
            }
            
            
            String dataEntrada = originalRow.getProperty("data_entrada");
            if (dataEntrada != null && dataEntrada.length() > 0){ //se data esta preenchida
                dataEntrada = dataEntrada.substring(6,10) + "-" + dataEntrada.substring(3,5) + "-" + dataEntrada.substring(0,2) + " " + dataEntrada.substring(11,16);
            }else{
                dataEntrada = "";
            }
            
            newRow.put("estado_contratacao", estado);
            newRow.put("comentarios_processo",comentarios);
            newRow.put("data_entrada", dataEntrada);
            
            workflowManager.processVariable(processId, "estado" , estado);
            newRows.add(newRow);
            
            String detalheProcessoPK = "#form.processos_hip.detalhe_processo["+ processId + "]#";
            detalheProcessoPK = AppUtil.processHashVariable(detalheProcessoPK,null,null,null);
            
            storeToOtherFormDataTable(appDef, element, newRows, formData, "detalheProcesso", detalheProcessoPK);
            LogUtil.info(appDef.toString(), "Importação de processo pendente ou reapreciação. Número de WF PL : " + n_wf_pl + " ProcessID: " + processId);
            
            workflowManager.processVariable(processIdSub, "estado" , estado);
            
        }
        

    }catch(Exception e){
        LogUtil.error("LEASING - CRIAR PROCESSO", e, "Error creating process");
    }
     
    return rows;
}


public void storeToOtherFormDataTable(AppDefinition appDef, Element element, FormRowSet rows, FormData formData, String formId, String id) {
    try{
        AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
        FormRowSet storedInfo = appService.storeFormData(appDef.getId(), appDef.getVersion().toString(), formId, rows, id);
    }catch(Exception e){
        LogUtil.error("LEASING - CRIAR PROCESSO", e, "Error storing data");
    }
    
}



  
//call execute method with injected variable
return storeData(element, rows, formData);
