}

public String calculateSLADuration(String dataInit, String dateEnd, Collection holidays){
    DateTimeFormatter formatter = DateTimeFormatter.ofPattern("dd/MM/yyyy HH:mm");
    DateTimeFormatter dateFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd");
    String result="";
    //if Data Init date format is not "dd/MM/yyyy HH:mm"
    //LogUtil.info("DATE INIT", "AAA" + dataInit);
    if(dataInit.charAt(2) != '/'){
        //if Data Init date format is of the format "yyyy-MM-dd HH:mm"
        if(dataInit.charAt(4) == '-' && dataInit.charAt(10) == ' ' && dataInit.charAt(13) == ':'){
            dataInit =  dataInit.substring(8,10) + "/" + dataInit.substring(5,7) + "/" + dataInit.substring(0,4) + " " + dataInit.substring(11,16);
        }
    }
    
    if(dateEnd.charAt(2) != '/'){
         //if Data Init date format is of the format "yyyy-MM-dd HH:mm"
        if(dateEnd.charAt(4) == '-' && dateEnd.charAt(10) == ' ' && dateEnd.charAt(13) == ':'){
            dateEnd =  dateEnd.substring(8,10) + "/" + dateEnd.substring(5,7) + "/" + dateEnd.substring(0,4) + " " + dateEnd.substring(11,16);
        }
    }

    LocalDateTime dateInit = LocalDateTime.parse(dataInit, formatter);
    LocalDateTime dateFinish = LocalDateTime.parse(dateEnd,formatter);
    
    long days = ChronoUnit.DAYS.between(dateInit, dateFinish);
    int initHours = dateInit.getHour();
    int finishHours = dateFinish.getHour();
    
    
    if(initHours > 12 && finishHours < 12){
        days= days - 1;    
    }else if(days>0 && initHours < 12 && finishHours >= 12){
        days = days + 1;
    }
    
    
    long workDays = days;
    
    while(days>0){
        
        if(holidays.contains(dateInit.format(dateFormatter))){
            workDays-=1;
            
        }else{
            String day = dateInit.getDayOfWeek().toString();
        
            switch(day) {
                case "SATURDAY":{
                    workDays-=1;
                    break;
                }
                case "SUNDAY":{
                    workDays-=1;
                    break;
                }
            }
        }
        

        dateInit = dateInit.plusDays(1);
  
        days-=1;
    }
    
    result = "D";
    if(workDays>0){
        result =result + "+" + workDays;
    }
    
    
    
    return result;
}

//Obter lista de feriados
public Collection getHolidays(Connection con){
    Collection holidays = new ArrayList(); 
    try{
        String queryGetHolidays = "SELECT * FROM app_fd_lista_feriados;";
        PreparedStatement stmt = con.prepareStatement(queryGetHolidays);
        ResultSet rs = stmt.executeQuery();
        while(rs.next()){
            holidays.add(rs.getObject("c_data"));
        }
    } catch(Exception e) {
        LogUtil.error("Load Binder - Resumo Procuradoria ", e, "Error getting holidays");
    } 
    
    return holidays;
}

//Verificar de o dia é fim de semana
public boolean isWeekend(LocalDateTime dt) {
    String dayOfWeek = dt.getDayOfWeek().toString();
    switch(dayOfWeek) {
        case "SATURDAY":{
            return true;
        }
        case "SUNDAY":{
            return true;
        }
        default:{
            return false;
        }
    }
}

  
