public boolean validate(Element element, FormData formData, String[] fileUploadValues) {
   
   //Get value from workflow variable
    String flagvalidate = "#variable.flag_ultima_atividade#";
    
    boolean result = true;
    
    //Only validate if it is the last activity of the subflow
    if(flagvalidate.equals("1")){
        //get dropdown select box value
        String field1_ex = "#form.detalhe_processo.tipo_processo[{form.processos.detalhe_processo}]#";
        String field1 = AppUtil.processHashVariable(field1_ex,null,null,null);
        
        String field2Id = "penhor";
        Form form = FormUtil.findRootForm(element);
        Element field2 = FormUtil.findElement(field2Id, form, formData);
        
        String field2Values = FormUtil.getElementPropertyValue(field2, formData);
      
        if (field1 != null) {
            //get value of field 1
            if(field1.contains("CVMH") && field2Values.isEmpty()){
                String id = FormUtil.getElementParameterName(element);
                formData.addFormError(id, "Campo obrigatório quando se trata de um processo CVMH");
                result = false;
            }
        } else {
            //ignore if the dropdown does not exist
        }
    }else{
        result = true;
    }
    return result;
}
  
