import org.joget.apps.form.model.Element;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.app.model.AppDefinition;
import org.joget.apps.app.service.AppService;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.service.FormUtil;
import org.joget.plugin.base.PluginManager;
import org.joget.apps.form.model.FormLoadBinder;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;
import org.joget.commons.util.LogUtil;
 
public FormRowSet load(Element element, String primaryKey, FormData formData) {
    String defaultFormDefId = "tipoDocumentosCheck"; //change this to the form id used to store default grid data
    String formDefId = "checkDocsImovel";  //change this to the form id used to store grid data
    String foreignKey = "fk"; //change this to the foreign key
     
    FormRowSet f = new FormRowSet();
    f.setMultiRow(true);
    
    // Reuse Multi Row Binder to load data
    PluginManager pluginManager = (PluginManager) FormUtil.getApplicationContext().getBean("pluginManager");
    FormLoadBinder binder = (FormLoadBinder) pluginManager.getPlugin("org.joget.plugin.enterprise.MultirowFormBinder");
     
    //Load from the grid table
    binder.setProperty("formDefId", formDefId);
    binder.setProperty("foreignKey", foreignKey);
    f = binder.load(element, primaryKey, formData);

    String forma_outorga = "#form.detalhe_processo.forma_outorga[{form.processos.detalhe_processo}]#";
    forma_outorga = AppUtil.processHashVariable(forma_outorga,null,null,null);
    String banco = "#form.detalhe_processo.nome_banco[{form.processos.detalhe_processo}]#";
    banco = AppUtil.processHashVariable(banco,null,null,null);
    
    //if( (!forma_outorga.equals("CAR") && !forma_outorga.equals("CPR")) || banco.equals("ABN") ){
        //if no grid data is retrieved, get from default table
        if (f == null || f.isEmpty()) {
            LogUtil.info("Analise Bancaria checklist", "Entra ");
            String tipo_processo = "#form.detalhe_processo.tipo_processo[{form.processos.detalhe_processo}]#";
            tipo_processo = AppUtil.processHashVariable(tipo_processo,null,null,null);
            LogUtil.info("Analise Bancaria checklist", "tipo_processo: " + tipo_processo);
            String[] tipos_processo = tipo_processo.split("\\+");
            
            Connection con = null;
            PreparedStatement stmt = null;
            ResultSet rs = null;
        
            try{
                DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                con = ds.getConnection();
                
                if(!con.isClosed()){
                    //Build sql query with the number of inputs needed based on loop iteration
                    String sql = "SELECT c_tipo_documento, c_checklist FROM app_fd_checklist_docs WHERE c_checklist = ? AND ( c_mostra LIKE ";
    
                    StringBuilder parameterBuilder = new StringBuilder();
                    
                    for (int i = 0; i < tipos_processo.length*3; i++) {
                        parameterBuilder.append("?");
                        if (tipos_processo.length*3 > i + 1) {
                            parameterBuilder.append("OR c_mostra LIKE");
                        }
                    }
                    parameterBuilder.append(")");
                    
                    //Here you can query from one or multiple tables using JOIN etc
                    PreparedStatement stmt = con.prepareStatement(sql + parameterBuilder);
                    stmt.setString(1, "processual");
                    
                    int count = 2;
                    //set values to the needed inputs
                    for(int i = 0; i < tipos_processo.length; i++){
                        String tipo_processo1 = tipos_processo[i] + ";%";
                        String tipo_processo2 = "%;" + tipos_processo[i] + ";%";
                        String tipo_processo3 = "%;" + tipos_processo[i];
                        //LogUtil.info("Analise Bancaria checklist", "inputs: " + tipo_processo1 + ", " + tipo_processo2 + ", " + tipo_processo3);
                        stmt.setString(count++, tipo_processo1);
                        stmt.setString(count++, tipo_processo2);
                        stmt.setString(count++, tipo_processo3);
                        
                        //count += 3;
                    }
                    //Execute the SELECT SQL statement
                    ResultSet rs = stmt.executeQuery();
                         
                    //Get value from columns of record(s)
                    while (rs.next()) {
                        //LogUtil.info("imovel", "entra");
                        FormRow r1 = new FormRow();
                        r1.put("tipo_documento", rs.getString(1));
                        r1.put("gestor", "");
                        r1.put("gestor_senior", "");
                        r1.put("validacaor", "");
                        r1.put("filtro", "");
                        f.add(r1);
                    }
                }
                    
            }catch(Exception e){
                LogUtil.error("Análise Bancária - Checklist", e, "Error getting info from DB");
            }finally{
                try{
                    if(rs != null){
                        rs.close();
                    }
                    if(stmt != null){
                        stmt.close();
                    }
                    if(con != null && !con.isClosed()) {
                        con.close();
                    }
                }catch(Exception e){
                    LogUtil.error("Análise Bancária - Checklist", e, "Error closing DB connection");
                }
                
            }
        }
    //}
    
    return f;
}
 
//call load method with injected variable
return load(element, primaryKey, formData);
