import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;
import org.joget.apps.form.service.FormUtil;
import org.joget.apps.app.model.AppDefinition;
import org.joget.commons.util.LogUtil;

 
public FormRowSet load(String[] values) {

    FormRowSet rows = new FormRowSet();
    
    FormRow option = new FormRow();
    option.setProperty(FormUtil.PROPERTY_VALUE, "");
    option.setProperty(FormUtil.PROPERTY_LABEL, "");
    rows.add(option);
    
    String banco = "#form.detalhe_proce_procu.banco_fundo[{form.processos_procu.detalhe_processo}]#";
    if (banco != null && !banco.isEmpty()) {
        banco = "%" + banco + "%";
    
        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
        String origem_value = "";
        String origem_label = "";
        
        AppDefinition appDef = AppUtil.getCurrentAppDefinition();
        
        try{
                DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                con = ds.getConnection();
                
                
                if(!con.isClosed()){
                    
                    String query="SELECT c_value, c_label FROM app_fd_listas_de_valores WHERE c_nome_campo = ? AND c_servico LIKE '%PROCU%' ORDER BY c_value;";
                    stmt = con.prepareStatement(query);
                    stmt.setObject(1, "estado_agendamento");
                    //stmt.setObject(2, banco);
                    rs = stmt.executeQuery();
                    
                    while(rs.next()){
                        origem_value = rs.getString(1);
                        origem_label = rs.getString(2);
                        
                        FormRow option = new FormRow();
                        option.setProperty(FormUtil.PROPERTY_VALUE, origem_value);
                        option.setProperty(FormUtil.PROPERTY_LABEL, origem_label);
                        
                        rows.add(option);
                        
                    }
                }
                
        }catch(Exception e){
            LogUtil.error("Importa processo - origem de processo", e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error("Importa processo - origem de processo", e, "Error closing DB connection");
            }
            
        }
        
    }

    return rows;
}
 
//call load method with injected variable
return load(values);

