public FormRowSet getStatusAgendamento() {
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    String status = "";
    String statusLabel = "";

    AppDefinition appDef = AppUtil.getCurrentAppDefinition();


    try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();

            if(!con.isClosed()){

                String idProcesso = "#form.processos_procu.id#";

                String queryGetLastAto = "SELECT c_status FROM app_fd_atos WHERE c_id_processo = ? ORDER BY dateCreated DESC LIMIT 1";
                stmt = con.prepareStatement(queryGetLastAto);
                stmt.setObject(1, idProcesso);
                rs = stmt.executeQuery();
                if(rs.next()){
                    status = rs.getObject("c_status");
                    String queryGetValueLabel = "SELECT c_label FROM app_fd_listas_de_valores WHERE c_nome_campo = 'estado_agendamento' AND c_value = ? ;";
                    stmt = con.prepareStatement(queryGetValueLabel);
                    stmt.setObject(1, status);
                    rs = stmt.executeQuery();
                    if(rs.next()){
                        statusLabel = rs.getObject("c_label");
                    }
                }

            }

    }catch(Exception e){
        LogUtil.error(appDef.toString(), e, "Error getting info from DB");
    }finally{
        try{
            if(rs != null){
            rs.close();
            }
            if(stmt != null){
                stmt.close();
            }
            if(con != null && !con.isClosed()) {
                con.close();
            }
        }catch(Exception e){
            LogUtil.error(appDef.toString(), e, "Error closing DB connection");
        }

    }
    FormRowSet f = new FormRowSet();
    FormRow r1 = new FormRow();
    r1.put(FormUtil.PROPERTY_VALUE, status);
    r1.put(FormUtil.PROPERTY_LABEL, statusLabel);
    f.add(r1);
    return f;
}

return getStatusAgendamento();

