}

public FormRowSet store(Element element, FormRowSet rows, FormData formData) {
       //check the rows is not empty before store it
       FormRow row = null;
    if (rows != null && !rows.isEmpty()) {
        //Get the submitted data
        row = rows.get(0);
        

        String ProcessID = "#process.processId#";
        String mainProcessId = rows.get(0).get("id_processo");
        String dataComInformal = rows.get(0).get("data_comunicacao_informal");
        String dataComunicacao = rows.get(0).get("data_conclusao");
        String dataFimSLA = "";
        if(dataComInformal.isEmpty()){
            if(!dataComunicacao.isEmpty()){
                dataFimSLA = dataComunicacao;
            }
             
        }else{
            dataFimSLA = dataComInformal;
        }
        String status = rows.get(0).get("status");
        
        
        boolean SLAIsOpen = false;
        
        
        //Caso exista uma data de Conclusão e o Ato tenha o estado de Outorgado ou Não Outorgado verificar o SLA para o fechar
        if(!dataFimSLA.isEmpty() && status.equals("OUT") || status.equals("NOUT")){
            Connection con = null;
            PreparedStatement stmt = null;
            ResultSet rs = null;
        
            try{
                
                DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                con = ds.getConnection();
                
                if(!con.isClosed()){    
                    
                    if(ProcessID.startsWith("#process.processId")){
                                                     
                        String queryGetProcessId = "SELECT Id FROM shkprocesses WHERE ActivityRequesterProcessId = ? order by created desc ;";
                        stmt = con.prepareStatement(queryGetProcessId);
                        stmt.setObject(1, mainProcessId);
                        rs = stmt.executeQuery();
                        if(rs.next()){
                            ProcessID = rs.getObject("Id");
                        }
                    
                    }
                    
                    
                    
                    //Verificar se existe algum SLA do processo em aberto
                    String queryGetOpenSLA = "SELECT id, c_data_inicio FROM app_fd_sla_atividade WHERE c_id_processo=? AND c_estado_atividade = 'Em curso';";
                    stmt = con.prepareStatement(queryGetOpenSLA);
                    stmt.setObject(1, ProcessID);
                    rs = stmt.executeQuery();
                    String idSLA = "";
                    String dataInicio = "";
                    if(rs.next()){
                        SLAIsOpen = true;
                        idSLA = rs.getObject("id");
                        dataInicio = rs.getObject("c_data_inicio");
                        
                    }
                    
                    
                    //Se existir SLA 'Em curso'
                    if(SLAIsOpen && !dataInicio.isEmpty()){
                        //Obter feriados para o cálculo do SLA
                        Collection holidays = getHolidays(con);
    
                        String slaDuration = calculateSLADuration(dataInicio, dataFimSLA,holidays);
                        //Fechar SLA 'Em curso'
                        String queryCloseSLA = "UPDATE app_fd_sla_atividade SET c_estado_atividade = 'Fechada', c_data_conclusao=? , c_duracao = ? WHERE id = ?;";
                        stmt = con.prepareStatement(queryCloseSLA);
                        stmt.setObject(1, dataFimSLA);
                        stmt.setObject(2, slaDuration);
                        stmt.setObject(3, idSLA);
                        rs = stmt.executeQuery();
    
                    }
                }
                
            }catch(Exception e){
                LogUtil.error("Olimpo- Store Binder Conclusões Procuradoria", e, "Error getting info from DB");
            
            }finally{
                try{
                    if(rs != null){
                    rs.close();
                    }
                    if(stmt != null){
                        stmt.close();
                    }
                    if(con != null && !con.isClosed()) {
                        con.close();
                    }
                }catch(Exception e){
                    LogUtil.error("Olimpo- Store Binder Conclusões Procuradoria", e, "Error closing DB connection");
                }
                
            }
        }
        

    
     //Reuse Workflow Form Binder to store data
        PluginManager pluginManager = (PluginManager) AppUtil.getApplicationContext().getBean("pluginManager");
        FormStoreBinder binder = (FormStoreBinder) pluginManager.getPlugin("org.joget.apps.form.lib.WorkflowFormBinder");
        binder.store(element, rows, formData);
    } 
    
    return rows;
}

public Collection getHolidays(Connection con){
    Collection holidays = new ArrayList(); 
    try{
        String queryGetHolidays = "SELECT * FROM app_fd_lista_feriados;";
        PreparedStatement stmt = con.prepareStatement(queryGetHolidays);
        ResultSet rs = stmt.executeQuery();
        while(rs.next()){
            holidays.add(rs.getObject("c_data"));
        }
    } catch(Exception e) {
        LogUtil.error("Load Binder - Resumo Procuradoria ", e, "Error getting holidays");
    } 
    
    return holidays;
}


