import java.util.Map;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collection;
import org.joget.apps.app.model.AppDefinition;
import org.joget.apps.app.service.AppService;
import org.joget.apps.app.service.AppUtil;
import org.joget.workflow.model.service.WorkflowManager;
import org.joget.workflow.model.WorkflowAssignment;
import org.joget.workflow.model.WorkflowProcess;
import org.joget.workflow.model.WorkflowProcessResult;
import org.joget.workflow.model.WorkflowActivity;
import org.joget.apps.form.model.Element;
import org.joget.apps.form.service.FormUtil;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.commons.util.UuidGenerator;
import org.joget.commons.util.LogUtil;
import org.joget.workflow.model.service.*;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;
import java.time.LocalDateTime; 
import java.time.format.DateTimeFormatter;
import java.util.regex.Pattern;
import java.util.regex.Matcher;
import java.time.LocalDate;
import java.time.DayOfWeek;
import java.time.temporal.ChronoUnit;


public FormRowSet storeData(Element element, FormRowSet rows, FormData formData) {

    AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
    WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
    AppDefinition appDef = AppUtil.getCurrentAppDefinition();
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;

    try {

        //check for empty data
        if (rows == null || rows.isEmpty()) {
            return rows;
        }

        //get value from form to set workflow variables
        FormRow originalRow = rows.get(0);
        String numero_processo = originalRow.getProperty("numero_processo");
        //String gestor = originalRow.getProperty("gestor");
        String dataEntrada = originalRow.getProperty("data_entrada");
        //String momentoEntrada = originalRow.getProperty("momento_entrada");
        String faseProcesso =  originalRow.getProperty("faseProcesso");
        String numeroAgenda = "";

        if (dataEntrada != null && dataEntrada.length() > 0) { // Se data estiver preenchida
            dataEntrada = dataEntrada.substring(6,10) + "-" + dataEntrada.substring(3,5) + "-" + dataEntrada.substring(0,2) + " " + dataEntrada.substring(11,16);
        } else {
            dataEntrada = "#date.yyyy-MM-dd HH:mm#"; // Caso a data não seja preenchida
        }

        int flagNumeroSequencial = 0;

        //Construção de número Agenda
        numeroAgenda = "HH" + numero_processo;

        /**Check if process exists**/
        boolean processExists = false;
        String processId = "";

        DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
        con = ds.getConnection();

        if(!con.isClosed()) { 
            String queryGetProcessId = "SELECT p.id FROM app_fd_processos_hh p JOIN app_fd_detalhe_proce_hh dp ON dp.id = p.c_detalhe_processo WHERE dp.c_n_agenda = ?";
            stmt = con.prepareStatement(queryGetProcessId);
            stmt.setObject(1, numeroAgenda);
            rs = stmt.executeQuery();

            if(rs.next()) {
                processId = rs.getObject("p.id");
                processExists = true;
            }
        }

        //Se o processo não existir
        if(!processExists) { 
            //start process with variables
            Map variables = new HashMap();
            variables.put("user_habilitacaoHerdeiros", "#currentUser.username#");

            //get process
            WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "HabilitacaoHerdeiros");

            //start process
            WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);

            //get process id
            processId = result.getProcess().getInstanceId();

            FormRowSet multipagedForms = new FormRowSet();
            FormRow formPrimaryKey = new FormRow();

            //definition of the fields of table processos where to store each generated primary key
            String[] fieldIds = {"detalhe_processo","tomada_conhecimento", "relacoes_bem", "libertacao_valores"};

            //definition of the formIds that correspond to the pages of the multipage form
            String[] formIds = {"detalheProcessoHH","tomadaConhecimentoHH", "relacoesdeBensHH", "libertacaoValoresHH"};

            for(int i = 0; i < fieldIds.length; i++) { 
                String foreignKey = UuidGenerator.getInstance().getUuid();
                formPrimaryKey.setProperty(fieldIds[i], foreignKey);

                FormRowSet newRows = new FormRowSet();
                FormRow newRow = new FormRow();

                if(fieldIds[i].equals("detalhe_processo")) { 
                    newRow.put("numero_processo", numero_processo);
                    newRow.put("data_entrada", dataEntrada);
                    newRow.put("faseProcesso", faseProcesso);
                    newRow.put("n_agenda", numeroAgenda);

                }else if(fieldIds[i].equals("tomada_conhecimento")) { 
                    newRow.put("numero_processo", numero_processo);
                    newRow.put("data_entrada", dataEntrada);
                   // newRow.put("fase_processo", faseProcesso);
                    newRow.put("n_agenda", numeroAgenda);

                } else if(fieldIds[i].equals("relacoes_bem")) {
                    newRow.put("numero_processo", numero_processo);
                    newRow.put("data_entrada", dataEntrada);
                  //  newRow.put("fase_processo", faseProcesso);
                    newRow.put("n_agenda", numeroAgenda);
                } else if(fieldIds[i].equals("libertacao_valores")){
                    newRow.put("numero_processo", numero_processo);
                    newRow.put("data_entrada", dataEntrada);
                   // newRow.put("fase_processo", faseProcesso);
                    newRow.put("n_agenda", numeroAgenda);
                }

                newRow.setProperty("id_processo", processId);
                newRows.add(newRow);

                storeToOtherFormDataTable(appDef, element, newRows, formData, formIds[i], foreignKey);
            }

            formPrimaryKey.setProperty("servico", "HH");
            multipagedForms.add(formPrimaryKey);

            storeToOtherFormDataTable(appDef, element, multipagedForms, formData, "HHProcesso", processId);

        } else {
            //Se o processo já existir

            FormRowSet newRows = new FormRowSet();
            FormRow newRow = new FormRow();

            newRow.put("data_entrada", dataEntrada);
            newRows.add(newRow);

            String detalheProcessoPK = "#form.processos_hh.detalhe_processo["+ processId + "]#";
            detalheProcessoPK = AppUtil.processHashVariable(detalheProcessoPK, null, null, null);

            storeToOtherFormDataTable(appDef, element, newRows, formData, "detalheProcessoHH", detalheProcessoPK);
        }

    } catch(Exception e) {
        LogUtil.error(appDef.toString(), e, "Habilitação Herdeiros | Erro ao criar processo");
    } finally {
        try {
            if(rs != null) {
                rs.close();
            }
            if(stmt != null) {
                stmt.close();
            }
            if(con != null && !con.isClosed()) {
                con.close();
            }
        } catch(Exception e) {
            LogUtil.error(appDef.toString(), e, "Habilitação Herdeiros - Criar Processo | Error closing DB connection");
        }
    }

    return rows;      
}


public void storeToOtherFormDataTable(AppDefinition appDef, Element element, FormRowSet rows, FormData formData, String formId, String id) {
    try{
        AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
        FormRowSet storedInfo = appService.storeFormData(appDef.getId(), appDef.getVersion().toString(), formId, rows, id);

    } catch(Exception e) {
        LogUtil.error(appDef.toString(), e, "Habilitação Herdeiros - Criar Processo | Error storing data");
    }
}


//call execute method with injected variable
return storeData(element, rows, formData);
