public boolean validate(Element element, FormData formData, String[] values){
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    
    boolean result = true;

    String id = FormUtil.getElementParameterName(element);
    //If element is not filled give error message
    if (values == null || values[0].isEmpty()){
        formData.addFormError(id, "Campo obrigatório");
        return false;
    }
    
    //Set fieldId of the field to compare
    String fieldId = "numero_processo";
    
    //Get element value
    Form form = FormUtil.findRootForm(element);
    Element field = FormUtil.findElement(fieldId, form, formData);
   //LogUtil.info("FIELD", "aaaaaaaaAA " + field);
    String fieldValue = FormUtil.getElementPropertyValue(field, formData);
    
    //Get porcesso_manual
    Element procManual = FormUtil.findElement("processo_manual", form, formData);
    //LogUtil.info("ProcManuel", "aaaaaaaaAA " + procManual);
    String processo_manual = FormUtil.getElementPropertyValue(procManual, formData);
    
    String numero_processo = values[0];
    
    try{ 
        //Construção de número Agenda
        String numeroAgenda = "HH" + numero_processo;
        
        /**Check if process exists**/
        boolean processExists = false;
        String processId = "";
        
        DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
        con = ds.getConnection();
        
        if(!con.isClosed()) { 
            String queryGetProcessId = "SELECT p.id FROM app_fd_processos_hh p JOIN app_fd_detalhe_proce_hh dp ON dp.id = p.c_detalhe_processo WHERE dp.c_n_agenda = ?";
            stmt = con.prepareStatement(queryGetProcessId);
            stmt.setObject(1, numeroAgenda);
            rs = stmt.executeQuery();
            
            if(rs.next()) {
                processId = rs.getObject("p.id");
                processExists = true;
            }
        }
        
        if(!processExists) { 
            result = true;    
        } else { 
            if (processo_manual.equals("SIM")) { 
                String id = FormUtil.getElementParameterName(element);
                formData.addFormError(id, "O processo já existe");
                result = false;    
            }
        }
        
    } catch(Exception e) {
        LogUtil.error(appDef.toString(), e, "Habilitação Herdeiros | Erro ao criar processo");
    } finally {
        try {
            if(rs != null) {
                rs.close();
            }
            if(stmt != null) {
                stmt.close();
            }
            if(con != null && !con.isClosed()) {
                con.close();
            }
        } catch(Exception e) {
            LogUtil.error(appDef.toString(), e, "Habilitação Herdeiros - Criar Processo | Error closing DB connection");
        }
    }
    
    
    return result;
}

//call validate method with injected variable
return validate(element, formData, values);

