    boolean result = true;
    
    //Only validate if it is the last activity of the subflow
    if(flagvalidate.equals("1")){
        
        //Configure field to validate - UPDATE HERE
        String fieldId = "data_fine_agendamento";
        
        //Get element value
        Form form = FormUtil.findRootForm(element);
        Element field = FormUtil.findElement(fieldId, form, formData);
        String fieldValue = FormUtil.getElementPropertyValue(field, formData);
        
        //If element is not filled give error message
        if (fieldValue == null || fieldValue.equals("")){
            String id = FormUtil.getElementParameterName(element);
            formData.addFormError(id, "Campo obrigatório");
            result = false;
        }else{
            result = true;
        }
    }else{
        result = true;
    }

    return result;

