                stmt = con.prepareStatement(queryGetOpenSLA);
                stmt.setObject(1, ProcessID);
                rs = stmt.executeQuery();
                
                if(rs.next()){
                    SLAIsOpen = true;
                }
                    
                //Se o estado do processo estiver Pendente
                if(EstadoContratacao.equals("PND")){
                    //Se o SLA estiver 'Em curso'
                   
                    if(SLAIsOpen){
                        
                        if(!dataComunicacaoCliente.isEmpty()){
                            //Obter data Inicial do SLA
                            String queryGetDateInitSla = "SELECT c_data_inicio FROM app_fd_sla_atividade WHERE c_id_processo=? AND c_estado_atividade = 'Em curso';";
