public FormRowSet store(Element element, FormRowSet rows, FormData formData) {
    if (rows == null) {
        return null;
    }

    FormRow firstRow = rows.iterator().next();
    
    if(!firstRow.get("documento").isEmpty() && firstRow.get("documento") != ""){
 
        try {
            String tableName = "docs_processo";
            String formDefId = "documentos";//"multiplosDocumentos";
            String foreignKey = "fk";
            String fileField = "documento";
             
            FormDataDao formDataDao = (FormDataDao) AppUtil.getApplicationContext().getBean("formDataDao");
             
            Form parentForm = FormUtil.findRootForm(element);
            String primaryKeyValue = parentForm.getPrimaryKeyValue(formData);
             
            
            Date currentDate = new Date();
            
            for (FormRow row : rows) {
                
                String uploadedDocuments = row.get("documento");
                String[] documentos = uploadedDocuments.split(";");
                String id_processo = row.get("id_processo");
                String id_doc = row.get("id_doc");
                String data_validade_documento = row.get("data_validade_documento");
                
                String uuid="";
                String initUuid = "";
                
                for(int i = 0; i<documentos.length; i++){
                    FormRowSet rowSet = new FormRowSet();
                    FormRow oneDocRow = new FormRow();
                    //row;
                    
                    uuid= UuidGenerator.getInstance().getUuid();
                    
                    row.setId(uuid);
                    
                        
                    // set created date
                    row.setDateCreated(currentDate);
                    
                    // set modified date
                    row.setDateModified(currentDate);
                    row.put("documento", documentos[i]);
                    row.put("nome_ficheiro", documentos[i].toString());
                    row.put("id_processo", id_processo);
                    row.put("id_doc", id_doc);
                    row.put("createdBy", "#currentUser.username#");
                    row.put("createdByName", "#currentUser.fullName#");
                    row.put("data_validade_documento", data_validade_documento);
                    
                    rowSet.add(row);
                    
                    //Check and update filename if exists
                    FileUtil.checkAndUpdateFileName(rowSet, tableName, uuid);
            
                    // save data
                    formDataDao.saveOrUpdate(formDefId, tableName, rowSet);

                    if(i>0){
                        String sourceDocs = "#envVariable.sourceDocumentsPath#";
                        File destPath = new File(sourceDocs + uuid);
                        destPath.mkdir();
                        File sourceFile = new File(sourceDocs+  initUuid + "\\" + documentos[i].toString());
                        sourceFile.renameTo(new File(destPath + "\\" + documentos[i].toString()));

                        
                    }else{
                    //store files
                    FileUtil.storeFileFromFormRowSet(rowSet, tableName, uuid);
                    initUuid = uuid;
                    }
                    
                    
                    
                }
                
               
            }


        } catch (Exception e) {
            LogUtil.error("File Handling in Bean Shell Binder Sample - Grid Form", e, "Store data error!!");
        }
    }
    return rows;
}
   
//call store method with injected variable
return store(element, rows, formData);

