import java.util.Arrays;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.model.Element;
import org.joget.apps.form.model.Form;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.service.FormUtil;
import org.joget.commons.util.LogUtil;
  
public boolean validate(Element element, FormData formData, String[] values){
    
    //Get value from workflow variable
    String flagvalidate = "#variable.flag_ultima_atividade#";
    
    boolean result = true;
    
    String faseProcesso = "#process.processId#";
    if(!faseProcesso.contains("Filtro")){
        return true;
    }
    
    String origem_processo = "#form.detalhe_processo.origem_processo[{form.processos.detalhe_processo}]#";
    if(!(origem_processo == "ACE" || origem_processo == "PRE" || origem_processo == "TRD")){
        return true;   
    }
    
    //Only validate if it is the last activity of the subflow
    if(flagvalidate.equals("1")){
        //If element is not filled give error message
        if(values == null || values[0].isEmpty()){
            String id = FormUtil.getElementParameterName(element);
            formData.addFormError(id, "Campo obrigatório");
            result = false;
        }
    }
    
    return result;

}

//call validate method with injected variable
return validate(element, formData, values);

