public boolean validate(Element element, FormData formData, String[] fileUploadValues) {
    boolean result = true;
    
    String faseProcesso = "#process.processId#";
    if(!faseProcesso.contains("Filtro")){
        return true;
        
    }
     
    String origem_processo = "#form.detalhe_processo.origem_processo[{form.processos.detalhe_processo}]#";
    if(!(origem_processo == "ACE" || origem_processo == "PRE" || origem_processo == "TRD")){
        return true;
    }
    
    
     
    //get dropdown select box value
    String field1Id = "urgencia_filtro";
    Form form = FormUtil.findRootForm(element);
    Element field1 = FormUtil.findElement(field1Id, form, formData);
    
     //get dropdown select box value
    String field2Id = "motivo_urgencia_filtro";
    Form form2 = FormUtil.findRootForm(element);
    Element field2 = FormUtil.findElement(field2Id, form2, formData);
    String field2Values = FormUtil.getElementPropertyValue(field2, formData);
  
    if (field1 != null) {
        //get value of field 1
        String field1Values = FormUtil.getElementPropertyValue(field1, formData);
  
        if(field1Values.equalsIgnoreCase("sim") && field2Values.isEmpty() ){
            String id = FormUtil.getElementParameterName(element);
            formData.addFormError(id, "Campo obrigatório quando existe Urgência de Filtro");
            result = false;
        }
    } else {
        //ignore if the dropdown does not exist
    }
    
    
  
    return result;
}
  
