import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.dao.FormDataDao;
import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.form.model.FormRow;
import org.joget.commons.util.LogUtil;
import org.joget.apps.form.service.FormUtil;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;

public FormRowSet load(String[] values) {
    FormRowSet rows = new FormRowSet();
     
    //set id_processo based on dependency value
    String id_processo = "";
    if (values != null && values.length > 0) {
        id_processo = values[0];
        //LogUtil.info("Beanshell document - imovel", "id_processo: " + id_processo);
        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
        try{
            //conect to DB
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                //get all fracoes of all the imoveis related to the received id_processo
                String query = "Select listProcess from (select c_tipo_processo_1 as listProcess, c_id_processo from app_fd_detalhe_processo in1 union all select c_tipo_processo_2 as listProcess, c_id_processo from app_fd_detalhe_processo in2 union all select c_tipo_processo_3 as listProcess, c_id_processo from app_fd_detalhe_processo in3 union all select c_tipo_processo_4 as listProcess, c_id_processo from app_fd_detalhe_processo in4 union all select c_tipo_processo_5 as listProcess, c_id_processo from app_fd_detalhe_processo in5) total where c_id_processo = ?";
                stmt = con.prepareStatement(query);
                stmt.setObject(1, id_processo);
                rs = stmt.executeQuery();
                
                int count = 0;
                if (rs.last()) {
                    count = rs.getRow();
                    rs.beforeFirst(); // not rs.first() because the rs.next() below will move on, missing the first element
                }
                if (count > 2){
                        //LogUtil.info("Beanshell document - imovel ", "Count: " + count);
                        //if we get more than one result from the query, add empty row
                        FormRow option = new FormRow();
                        option.setProperty(FormUtil.PROPERTY_VALUE, "");
                        option.setProperty(FormUtil.PROPERTY_LABEL, "");
                        rows.add(option);
                }
                
                while (rs.next()) {
                    if(rs.getString(1) != null){
                        String opcao = rs.getString(1);
                        FormRow option = new FormRow();
                        option.setProperty(FormUtil.PROPERTY_VALUE, opcao);
                        option.setProperty(FormUtil.PROPERTY_LABEL, opcao);
                        rows.add(option);
                    }
                }
            }
            }catch(Exception e){
                LogUtil.error("Beanshell document - imovel", e, "Error getting info from DB");
            }finally{
                try{
                    if(rs != null){
                    rs.close();
                    }
                    if(stmt != null){
                        stmt.close();
                    }
                    if(con != null && !con.isClosed()) {
                        con.close();
                    }
                }catch(Exception e){
                    LogUtil.error("Beanshell document - imovel", e, "Error closing DB connection");
                }
                
            }
    }
    return rows;
}
 
//call load method with injected variable
return load(values);

import org.joget.apps.form.model.Element;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.app.model.AppDefinition;
import org.joget.apps.app.service.AppService;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.service.FormUtil;
import org.joget.plugin.base.PluginManager;
import org.joget.apps.form.model.FormLoadBinder;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;
import org.joget.commons.util.LogUtil;
 
public FormRowSet load(Element element, String primaryKey, FormData formData) {
    String defaultFormDefId = "tipoDocumentosCheck"; //change this to the form id used to store default grid data
    String formDefId = "checkDocsImovel";  //change this to the form id used to store grid data
    String foreignKey = "fk"; //change this to the foreign key
     
    FormRowSet f = new FormRowSet();
    f.setMultiRow(true);
    
    // Reuse Multi Row Binder to load data
    PluginManager pluginManager = (PluginManager) FormUtil.getApplicationContext().getBean("pluginManager");
    FormLoadBinder binder = (FormLoadBinder) pluginManager.getPlugin("org.joget.plugin.enterprise.MultirowFormBinder");
     
    //Load from the grid table
    binder.setProperty("formDefId", formDefId);
    binder.setProperty("foreignKey", foreignKey);
    f = binder.load(element, primaryKey, formData);
     
    //if no grid data is retrieved, get from default table
    if (f == null || f.isEmpty()) {
        String tipo_processo = "#form.detalhe_processo.tipo_processo[{form.processos.detalhe_processo}]#";
        String[] tipos_processo = tipo_processo.split("\\+");
        LogUtil.info("imovel", "tipo de processo: " + tipo_processo);
        
        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
    
        try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                //Build sql query with the number of inputs needed based on loop iteration
                String sql = "SELECT id, c_tipo_documento FROM app_fd_checklist_docs WHERE c_mostra IN";

                StringBuilder parameterBuilder = new StringBuilder();
                parameterBuilder.append(" (");
                for (int i = 0; i < tipos_processo.length*3; i++) {
                    parameterBuilder.append("?");
                    if (tipos_processo.length > i + 1) {
                        parameterBuilder.append(",");
                    }
                }
                parameterBuilder.append(")");
                
                LogUtil.info("imovel", "sql: " + sql);
                //Here you can query from one or multiple tables using JOIN etc
                PreparedStatement stmt = con.prepareStatement(sql + parameterBuilder);
                
                int count = 0;
                //set values to the needed inputs
                for(int i = 0; i < tipos_processo.length; i++){
                    String tipo_processo1 = tipos_processo[i] + ";%";
                    String tipo_processo2 = "%;" + tipos_processo[i] + ";%";
                    String tipo_processo3 = "%;" + tipos_processo[i];
                    LogUtil.info("imovel", "inputs: " + tipo_processo1 + ", " + tipo_processo2 + ", " + tipo_processo3);
                    stmt.setString(count+1, tipo_processo1);
                    stmt.setString(count+2, tipo_processo2);
                    stmt.setString(count+3, tipo_processo3);
                    
                    count += 3;
                }
                //Execute the SELECT SQL statement
                ResultSet rs = stmt.executeQuery();
                     
                //Get value from columns of record(s)
                while (rs.next()) {
                    LogUtil.info("imovel", "entra");
                    FormRow r1 = new FormRow();
                    r1.put("doc_id", rs.getString(1));
                    r1.put("tipo_documento", rs.getString(2));
                    r1.put("gestor", "");
                    r1.put("gestor_senior", "");
                    r1.put("validacaor", "");
                    r1.put("filtro", "");
                    f.add(r1);
                }
            }
                
        }catch(Exception e){
            LogUtil.error("imovel", e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                    rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error("imovel", e, "Error closing DB connection");
            }
            
        }
    }
    return f;
}
 
//call load method with injected variable
return load(element, primaryKey, formData);
