String id = FormUtil.getElementParameterName(element);
String number = "";
if(values.length > 0){
    number = values[0];
}
if (!number.isEmpty()){
    final int max=9;
    //check if is numeric and has 9 numbers
    if (!number.matches("[0-9]+") || number.length()!=max){
        formData.addFormError(id, "NIF deve ter 9 digitos");
        return false;
    }

    int checkSum=0;
    //calculate checkSum
    for (int i=0; i<max-1; i++){
    	checkSum+=(number.charAt(i)-'0')*(max-i);
    }
    int checkDigit=11-(checkSum % 11);
    //if checkDigit is higher than 9 set it to zero
    if (checkDigit>9) checkDigit=0;
    //compare checkDigit with the last number of NIF
    if (checkDigit!=number.charAt(max-1)-'0'){
        formData.addFormError(id, "NIF inválido");
        return false;
    }
}
