public String calculateSLAD(String dataInicio, Collection feriados){
    
    DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm");
    DateTimeFormatter dateFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd");
    LocalDateTime dateInit = LocalDateTime.parse(dataInicio, formatter);  
    LocalDateTime endDateTime = null;
    String endDate = "";
    Collection holidays = feriados;
    
    
    var hour = dateInit.getHour();

    if(hour < 12){
        endDateTime = dateInit.withHour(23).withMinute(59);
        while(isWeekend(endDateTime) || holidays.contains(endDateTime.format(dateFormatter))){
           endDateTime = endDateTime.plusDays(1);
        }
        endDate = endDateTime.format(formatter);
    }else{
        endDateTime = dateInit.plusDays(1).withHour(12).withMinute(0);
        while(isWeekend(endDateTime) || holidays.contains(endDateTime.format(dateFormatter))){
           endDateTime = endDateTime.plusDays(1);
        }
        endDate = endDateTime.format(formatter);
    }
    
    return endDate;
}

public String durationSLA(String dataInicio){
    var duracao="";
    DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm");
    LocalDateTime dateInit = LocalDateTime.parse(dataInicio, formatter); 
    var hour = dateInit.getHour();
    
    if(hour < 12){
        duracao="D";
    }else{
        duracao="D+1";
    }
    
    return duracao;
}

public boolean isWeekend(LocalDateTime dt) {
    String dayOfWeek = dt.getDayOfWeek().toString();

    switch(dayOfWeek) {
        case "SATURDAY":{
            return true;
        }
        case "SUNDAY":{
            return true;
        }
        default:{
            return false;
        }
    }
}

public String addOneHour(String data){
    DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm");
    LocalDateTime date = LocalDateTime.parse(data, formatter);
    
    String formattedDateTime = date.format(formatter);
   
    String correctDate = "";
    
    correctDate = date.plusHours(1).format(formatter);

    
    return correctDate;
}


public String decreaseOneHour(String data){
    DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm");
    LocalDateTime date = LocalDateTime.parse(data, formatter);
    
    String correctDate = "";
    
    correctDate = date.minusHours(1).format(formatter);
    
    return correctDate;
}

public FormRowSet storeData(Element element, FormRowSet rows, FormData formData) {
    AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
    WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
    AppDefinition appDef = AppUtil.getCurrentAppDefinition();
        
    try{
        //check for empty data
        if (rows == null || rows.isEmpty()) {
            return rows;
        }
        
        //get value from form to set workflow variables
        FormRow originalRow = rows.get(0);
        bancoValor = originalRow.getProperty("nome_banco");
        complexidadeValor = "NOR";
        nWFBanco = originalRow.getProperty("n_wf_banco");
        comentarios = originalRow.getProperty("comentarios_processo");
        
        /**switch date format
         * Date format received: 14/10/2022 16:00
         * Date format needed: 2022-10-14 13:00
         * **/
        //data format we receive: 14/10/2022 16:00
        //data format we need: 2022-10-14 13:00
        String receivedDate = originalRow.getProperty("data_entrada");
        String finalDate;
        if (receivedDate != null && receivedDate.length() > 0){ //se data esta preenchida
            finalDate = receivedDate.substring(6,10) + "-" + receivedDate.substring(3,5) + "-" + receivedDate.substring(0,2) + " " + receivedDate.substring(11,16);
        }else{
            /**ALTERAR QUANDO SE ALTERAREM OS SLA*/
            finalDate = "#date.yyyy-MM-dd HH:mm#";
        }
        
        String n_wf_pl = "";
        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
        boolean existe = false;
        String processId = null;
        
        /**create n_wf_pl
         * If the flag to automatically generate the number is active then we need to obtain the maximum number generated untill now and increment that value
         * TODO - TOrnar automatico o mecanismo de de ir buscar o numero maximo existente
         * **/
         
        if(bancoValor.equals("GEN")){
            //Get maximum valu from DB
            try{
                DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                con = ds.getConnection();
                
                if(!con.isClosed()){
                    //Set query
                    String query = "SELECT Max(replace(c_n_wf_pl, 'PLDPA', '')) as num_max FROM app_fd_processos p JOIN app_fd_detalhe_processo where c_n_wf_pl like 'PLDPA%' and length(c_n_wf_pl) = 15";
                    stmt = con.prepareStatement(query);
                    rs = stmt.executeQuery();
                    
                    //Get Value from result set
                    if (rs.next()) {
                        nWFBanco = rs.getString(1);
                    }else{
                        nWFBanco = "0";
                    }
                }
            }catch(Exception e){
                LogUtil.error("appDef.toString()", e, "Error getting info from DB");
            }finally{
                try{
                    if(rs != null){
                        rs.close();
                    }
                    if(stmt != null){
                        stmt.close();
                    }
                    if(con != null && !con.isClosed()) {
                        con.close();
                    }
                }catch(Exception e){
                    LogUtil.error("appDef.toString()", e, "Error closing DB connection");
                }
                
            }
            
            //create final number of WF
            nWFBanco = String.valueOf(Integer.valueOf(nWFBanco) + 1);
            nWFBanco = "000000000" + nWFBanco;
            nWFBanco = nWFBanco.substring(nWFBanco.length() - 10);
            
            n_wf_pl = "PLDPA" + nWFBanco;
            
            
            LogUtil.info(appDef.toString(), "Gerar número de processo automaticamente. Número de WF PL gerado: " + n_wf_pl);
        }else if(bancoValor.equals("MTG")){
            n_wf_pl = "MG" + "DPA" + nWFBanco;
            //LogUtil.info("Importa Processo", "N WF PL: " + n_wf_pl);
        }else if(bancoValor.equals("BST")){
            n_wf_pl = bancoValor + nWFBanco;
            //LogUtil.info("Importa Processo", "N WF PL: " + n_wf_pl);
        }else if(bancoValor.equals("BIC")){
            String[] numero = nWFBanco.split("/");
            if(numero.length == 2){
                nWFBanco = numero[0] + numero[1];
            }
            n_wf_pl = bancoValor + "DPA" + nWFBanco;
            //LogUtil.info("Importa Processo", "N WF PL: " + n_wf_pl);
        }else if(bancoValor.equals("NVB")){
            n_wf_pl = "NB" + "DPA" + nWFBanco.substring(4,nWFBanco.length());
            //LogUtil.info("Importa Processo", "N WF PL: " + n_wf_pl);
        }else if(bancoValor.equals("ABN")){
            if(originalRow.getProperty("origem_processo").equals("PRE")){
                n_wf_pl = "AB" + "DPAP" + nWFBanco;
            }else{
                n_wf_pl = "AB" + "DPAp" + nWFBanco;
            }
            nWFBanco = "EE " + nWFBanco;
        }else{
            n_wf_pl = bancoValor + "DPA" + nWFBanco;
            //LogUtil.info("Importa Processo", "N WF PL: " + n_wf_pl);
        }
        
        /**Check if the n_wf_pl already exists in the DB
         * if exists boolean existe = true
         * **/
        
        try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                String query = "SELECT p.id FROM app_fd_processos p JOIN app_fd_detalhe_processo dp ON dp.id = p.c_detalhe_processo WHERE dp.c_n_wf_pl = ?";
                stmt = con.prepareStatement(query);
                stmt.setObject(1, n_wf_pl);
                rs = stmt.executeQuery();
                if (rs.next()) {
                    processId = rs.getString(1);
                    String queryGetProcess = "select subpro.id, subpro.PDefName, ass.activityID, ass.ResourceID  from shkprocesses pro left join shkprocesses subpro on pro.id = subpro.activityRequesterProcessID left join shkassignmentstable ass on subpro.id = ass.ActivityProcessID where pro.id = ? and activityID is not null";
                    stmt = con.prepareStatement(queryGetProcess);
                    stmt.setObject(1, processId);
                    rs = stmt.executeQuery();
                    if(rs.next()){
                       existe = true;
                    } 
                    
                }
            }
        }catch(Exception e){
            LogUtil.error(appDef.toString(), e, "Error getting info from DB when trying to get a process by n_wf_pl");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error(appDef.toString(), e, "Error closing DB connection");
            }
            
        }
        /** Create new process 
         * estado_contratacao = "NOV" by default
         * put the values of the workflow variables needed into "variables"
         * if the process is starting manually it is allocated to the current user
         * start a new process of type "CreditoHabitacao" with "variables"
         * */
        if(!existe){
            
            String estado = "";
            if(originalRow.getProperty("estado_contratacao") == null || originalRow.getProperty("estado_contratacao").isEmpty()){
                estado = "NOV";
            }else{
                estado = originalRow.getProperty("estado_contratacao");
            }
            
            //start process with variables
            Map variables = new HashMap();
            variables.put("banco", bancoValor);
            variables.put("estado", estado);
            variables.put("complexidade", complexidadeValor);
            
            String processo_manual = originalRow.getProperty("processo_manual");
            
            //Verify if the process is starting manually and if so allocates the process to the current User
            if (processo_manual != null && processo_manual.equals("SIM")){
                String user_variable = "";
                if (bancoValor.equals("MTG")){
                    user_variable = "user_contratacao_" + bancoValor.toLowerCase() + "1"; //creates the variable name used on the task allocation plugin
                }else{
                    user_variable = "user_contratacao_" + bancoValor.toLowerCase();       //creates the variable name used on the task allocation plugin
                }
                variables.put(user_variable, "#currentUser.username#");
            }
            
            //get process
            WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "CreditoHabitacao");
            //start process
            WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);
            //get process id
            processId = result.getProcess().getInstanceId();
            
            /**TODO*/
            /*LogUtil.info(appDef.toString(), "processId: " + processId);
            String[] processo = processId.split("_");
            int id = Integer.parseInt(processo[0]) + 1;
            String subProcess = id + "_" + processo[1] + "_CHContratacaoNormal";
            LogUtil.info(appDef.toString(), "subProcess: " + subProcess);
            ArrayList activity = workflowManager.getActivityList(subProcess, null, null, null, null);
            LogUtil.info(appDef.toString(), "activity ID: " + activity);
            */
            
            /** Store fields in DB
             * generate UUID for each column of app_fd_processos corresponding to a table (fieldIds)
             * create an entry in every table with the formId in (formIds) with the value of the column id_processo and store other respective fields if any
             * create an interveniente if either the nif or nome is filled
             * create an entry in app_fd_processos with all the UUID foerign keys generated
             * */
            
            FormRowSet multipagedForms = new FormRowSet();
            FormRow formPrimaryKey = new FormRow();
            
            //definition of the fields of table processos where to store each generated primary key
            String[] fieldIds = {"detalhe_processo", "intervenientes", "docs", "imoveis", "banco", "anal_banc", "anal_legal", "esclarecimentos", "comunicacoes", "geral"};
            //definition of the formIds that correspond to the pages of the multipage form
            String[] formIds = {"detalheProcesso", "intervenientes", "documentos", "imoveis", "banco", "analiseBancaria", "analiseLegal", "esclarecimentos", "comunicacoes", "geral"};
            
            //Iterate through the different forms where we need to store each field
            for(int i = 0; i < fieldIds.length; i++){
                String foreignKey = UuidGenerator.getInstance().getUuid();
                formPrimaryKey.setProperty(fieldIds[i], foreignKey);
    
                FormRowSet newRows = new FormRowSet();
                FormRow newRow = new FormRow();
                if(fieldIds[i].equals("detalhe_processo") ){
                    newRow.put("complex_processo", complexidadeValor);
                    newRow.put("fase_processo", "CNT");
                    newRow.put("nome_banco", bancoValor);
                    newRow.put("data_entrada", finalDate);
                    newRow.put("forma_outorga", originalRow.getProperty("forma_outorga"));
                    newRow.put("origem_processo", originalRow.getProperty("origem_processo"));
                    newRow.put("estado_contratacao", estado);
                    newRow.put("criado_por", "#currentUser.username#");
                    newRow.put("n_wf_pl", n_wf_pl);
                    newRow.put("urgencia", "NAO");
                    newRow.put("comentarios_processo",comentarios);
                    
                    //if it's a process with a bank associated
                    if(!n_wf_pl.contains("PLDPA")){
                        newRow.put("n_wf_banco", nWFBanco);
                    }
                    
                }else if(fieldIds[i].equals("banco") ){
                    newRow.put("nome_banco", bancoValor);
                    
                }else if(fieldIds[i].equals("intervenientes") ){
                    String intervenienteId = UuidGenerator.getInstance().getUuid();
                    if(!originalRow.getProperty("nome").isEmpty() || !originalRow.getProperty("nif").isEmpty()){
                        FormRowSet newGridRows = new FormRowSet();
                        FormRow newGridRow = new FormRow();
                        
                        newGridRow.put("id_intervenientes", foreignKey);
                        newGridRow.put("nome", originalRow.getProperty("nome"));
                        newGridRow.put("nif", originalRow.getProperty("nif"));
                        newGridRow.put("id_processo", processId);
                        newGridRow.put("cliente_principal", "CHECK");
                        newGridRows.add(newGridRow);
                        storeToOtherFormDataTable(appDef, element, newGridRows, formData, "interveniente", intervenienteId);
                    }
                }
                newRow.setProperty("id_processo", processId);
                newRows.add(newRow);
                storeToOtherFormDataTable(appDef, element, newRows, formData, formIds[i], foreignKey);
            }
            multipagedForms.add(formPrimaryKey);
            
            storeToOtherFormDataTable(appDef, element, multipagedForms, formData, "processo", processId);
            
            // Criar SLA com estado a 'Em curso'
            if(estado!="PND"){
                String queryGetAssignment = "SELECT id, name, resourcerequesterid  FROM shkprocesses WHERE activityrequesterprocessid =? AND id like '%contratacao%' order by created desc;";
                String queryGetUserAssign = "SELECT resourceid FROM shkassignmentstable WHERE activityprocessid =?";
                String idSubProcesso= "";
                String nomeAtividade = "";
                String utilizador = "";
                Connection con2=null;
                Collection holidays = new ArrayList(); 
                try{
                    DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                    con2 = ds.getConnection();
                    if(!con2.isClosed()){
                        
                        stmt = con2.prepareStatement(queryGetAssignment);
                        stmt.setObject(1, processId);
                        rs = stmt.executeQuery();
                        
                        if(rs.next()){
                            idSubProcesso = rs.getObject("id").toString();
                            nomeAtividade= rs.getObject("name").toString();
                        }
                        
                        stmt = con2.prepareStatement(queryGetUserAssign);
                        stmt.setObject(1, idSubProcesso);
                        rs = stmt.executeQuery();
                        
                        if(rs.next()){
                            utilizador= rs.getObject("resourceid").toString();
                        }
                        
                        
                        //Obter feriados
                            String queryGetHolidays = "SELECT * FROM app_fd_lista_feriados;";
                            stmt = con2.prepareStatement(queryGetHolidays);
                            rs = stmt.executeQuery();
      
                            while(rs.next()){
                                holidays.add(rs.getObject("c_data"));
                            }
                        
                        
                    }
                }catch(Exception e){
                    LogUtil.error(appDef.toString(), e, "Error getting info from DB");
                }finally{
                    try{
                        if(rs != null){
                        rs.close();
                        }
                        if(stmt != null){
                            stmt.close();
                        }
                        if(con2 != null && !con2.isClosed()) {
                            con2.close();
                        }
                    }catch(Exception e){
                        LogUtil.error(appDef.toString(), e, "Error closing DB connection");
                    }
                }
                    
            var idSLA = UuidGenerator.getInstance().getUuid();
            var estadoAtividade = "Em curso";
            //alteracao devido a mudanca do formato da data recebida
            var dataInicio = finalDate; 
            String dataFim = calculateSLAD(dataInicio, holidays);
            String duracao =  durationSLA(dataInicio);
            FormRowSet rowSetSLA = new FormRowSet();
            FormRow rowsSLA = new FormRow();
            rowsSLA.put("id_processo", idSubProcesso);
            rowsSLA.put("nome_atividade", nomeAtividade);
            rowsSLA.put("data_inicio", dataInicio);
            rowsSLA.put("data_fim", dataFim);
            rowsSLA.put("utilizador_atribuido", utilizador);
            rowsSLA.put("estado_atividade", estadoAtividade);
            rowsSLA.put("duracao", duracao);
            rowSetSLA.add(rowsSLA);
            storeToOtherFormDataTable(appDef, element, rowSetSLA, formData, "slaAtividade", idSLA);
        }
        
        LogUtil.info(appDef.toString(), "Novo Processo Criado com sucesso. Número de WF PL gerado: " + n_wf_pl);
                
        }else{ //if the process already exists
            LogUtil.info(appDef.toString(), "Número de Workflow já existente em sistema");
            FormRowSet newRows = new FormRowSet();
            FormRow newRow = new FormRow();
            
            String estado = "";
            if(originalRow.getProperty("estado_contratacao") == null || originalRow.getProperty("estado_contratacao").length() <= 0){
                estado = "DEF";
            }else{
                estado = originalRow.getProperty("estado_contratacao");
            }
            newRow.put("estado_contratacao", estado);
            newRow.put("comentarios_processo",comentarios);
            workflowManager.processVariable(processId, "estado" , estado);
            newRows.add(newRow);
            
            String detalheProcessoPK = "#form.processos.detalhe_processo["+ processId + "]#";
            detalheProcessoPK = AppUtil.processHashVariable(detalheProcessoPK,null,null,null);
            
            storeToOtherFormDataTable(appDef, element, newRows, formData, "detalheProcesso", detalheProcessoPK);
            LogUtil.info(appDef.toString(), "Importação de processo pendente ou reapreciação. Número de WF PL : " + n_wf_pl + " ProcessID: " + processId);
            
            /*Force activity to continue -*/
            String activityId;
            String processIdSub;
            String processDefId;
            String username;
            
            //Get info from DB
            try{
                DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                con = ds.getConnection();
                
                if(!con.isClosed()){
                    //Set query
                    String queryGetActivityInfo = "select subpro.id, subpro.PDefName, ass.activityID, ass.ResourceID  from shkprocesses pro left join shkprocesses subpro on pro.id = subpro.activityRequesterProcessID left join shkassignmentstable ass on subpro.id = ass.ActivityProcessID where pro.id = ? and activityID is not null";
                    stmt = con.prepareStatement(queryGetActivityInfo);
                    stmt.setObject(1, processId);
                    rs = stmt.executeQuery();
                    
                    //Get Value from result set
                    if (rs.next()) {
                        processIdSub = rs.getString(1);
                        processDefId = rs.getString(2);
                        activityId = rs.getString(3);
                        username = rs.getString(4) ;
                    }else{
                         LogUtil.info("Process Store Binder:","Process not found!");
                    }
                }
            }catch(Exception e){
                LogUtil.error("appDef.toString()", e, "Error getting info from DB");
            }finally{
                try{
                    if(rs != null){
                        rs.close();
                    }
                    if(stmt != null){
                        stmt.close();
                    }
                    if(con != null && !con.isClosed()) {
                        con.close();
                    }
                }catch(Exception e){
                    LogUtil.error("appDef.toString()", e, "Error closing DB connection");
                }
                
            }
            
            workflowManager.processVariable(processIdSub, "estado" , "DEF");
            
            /*stop the activity and continue process*/
            workflowManager.assignmentForceComplete(processDefId, processIdSub, activityId, username);
            
        }
        

    }catch(Exception e){
        LogUtil.error(appDef.toString(), e, "Error creating process");
    }
     
    return rows;
}


public void storeToOtherFormDataTable(AppDefinition appDef, Element element, FormRowSet rows, FormData formData, String formId, String id) {
    try{
        AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
        FormRowSet storedInfo = appService.storeFormData(appDef.getId(), appDef.getVersion().toString(), formId, rows, id);
    }catch(Exception e){
        LogUtil.error(appDef.toString(), e, "Error storing data");
    }
    
}



  
//call execute method with injected variable
return storeData(element, rows, formData);
