import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.model.Element;
import org.joget.apps.form.model.Form;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.form.model.defaultHashVariablePlugin;
import org.joget.apps.form.service.FormUtil;
import org.joget.commons.util.LogUtil;
import java.util.ArrayList;

 
public FormRowSet load(Element element, String primaryKey, FormData formData) {
    //LogUtil.info("Element ----->", element.toString());
    FormRowSet rows = new FormRowSet();
    
    //String cUserDepartmentName = "#currentUser.department.name#";
    String cUserDepartmentID = "#currentUser.department.id#";
    
    //String[] cUserDepName = cUserDepartmentName.split(";");
    String[] cUserDepID = cUserDepartmentID.split(";");
    bancos = new ArrayList();
    for(int i = 0; i < cUserDepID.length; i++ ){
        if(cUserDepID[i].length() > 11){
            String value = cUserDepID[i].substring(11,14);
            if(!bancos.contains(value)){
                bancos.add(value);
            }
            //LogUtil.info("Select equipa ----->", "Entra:" + equipa);
        }
    }
    
    if(bancos.size() > 1){
        FormRow option = new FormRow();
        option.setProperty(FormUtil.PROPERTY_VALUE, "");
        option.setProperty(FormUtil.PROPERTY_LABEL, "");
        rows.add(option);
    }
    for(String banco : bancos){
        LogUtil.info("Select banco ----->", banco);
        //if currentUser belongs to more than 1 bank, add empty option to ensure that the right one is chosen
        FormRow option = new FormRow();
        option.setProperty(FormUtil.PROPERTY_VALUE, banco);
        option.setProperty(FormUtil.PROPERTY_LABEL, banco);
        
        rows.add(option);
    }

    return rows;
}
 
//call load method with injected variable
return load(element, primaryKey, formData);

import java.util.Map;
import org.joget.apps.app.model.AppDefinition;
import org.joget.apps.app.service.AppService;
import org.joget.apps.app.service.AppUtil;
import org.joget.workflow.model.service.WorkflowManager;
import org.joget.workflow.model.WorkflowAssignment;
import org.joget.workflow.model.WorkflowProcess;
import org.joget.workflow.model.WorkflowProcessResult;
import org.joget.apps.form.model.Element;
import org.joget.apps.form.service.FormUtil;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.commons.util.UuidGenerator;
import org.joget.commons.util.LogUtil;
import org.joget.workflow.model.service.*;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;
  
public FormRowSet storeData(Element element, FormRowSet rows, FormData formData) {
    try{
        LogUtil.info("Element ----->", element.toString());
        //check for empty data
        if (rows == null || rows.isEmpty()) {
            return rows;
        }
        
        AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
        WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
        AppDefinition appDef = AppUtil.getCurrentAppDefinition();
        
        //get value from form to set workflow variables
        FormRow originalRow = rows.get(0);
        bancoValor = originalRow.getProperty("nome_banco");
        complexidadeValor = originalRow.getProperty("complex_processo");
        nWFBanco = originalRow.getProperty("n_wf_banco");
        //LogUtil.info("Beanshell import", "bancoValor: " + bancoValor);
        //LogUtil.info("Beanshell import", "complexidadeValor: " + complexidadeValor);
        
        //create n_wf_pl
        String n_wf_pl = bancoValor + "DPA" + nWFBanco;
        
        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
        boolean existe = false;
        
        try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                String query = "SELECT p.id FROM app_fd_processos p JOIN app_fd_detalhe_processo dp ON dp.id = p.c_detalhe_processo WHERE dp.c_n_wf_pl = ?";;
                stmt = con.prepareStatement(query);
                stmt.setObject(1, n_wf_pl);
                rs = stmt.executeQuery();
                //LogUtil.info("Beanshell import ---------", "1");
                if (rs.next()) {
                    existe = true;
                    //LogUtil.info("Beanshell import ---------", existe.toString());
                }
            }
        }catch(Exception e){
            LogUtil.error(appDef.toString(), e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error(appDef.toString(), e, "Error closing DB connection");
            }
            
        }
        
        if(!existe){
            //get process
            WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "CreditoHabitacao");
            //LogUtil.info(appDef.toString(), "ProcessDefId: " + process.getId());
            
            //start process with variables
            Map variables = new HashMap();
            variables.put("banco", bancoValor);
            variables.put("complexidade", complexidadeValor);
            WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);
            
            LogUtil.info("Result", "" + result.getProcess());
            
            //get current record id
            String processId = result.getProcess().getInstanceId();
            LogUtil.info(appDef.toString(), "Process id = " + processId);
            
            FormRowSet multipagedForms = new FormRowSet();
            FormRow formPrimaryKey = new FormRow();
            
            String[] fieldIds = {"detalhe_processo", "intervenientes", "docs", "imoveis", "banco", "anal_banc", "anal_legal", "esclarecimentos", "comunicacoes", "geral"};
            String[] formIds = {"detalheProcesso", "intervenientes", "documentos", "imoveis", "banco", "analiseBancaria", "analiseLegal", "esclarecimentos", "comunicacoes", "geral"};
    
            for(int i = 0; i < fieldIds.length; i++){
                String foreignKey = UuidGenerator.getInstance().getUuid();
                formPrimaryKey.setProperty(fieldIds[i], foreignKey);
    
                FormRowSet newRows = new FormRowSet();
                FormRow newRow = new FormRow();
                if(fieldIds[i].equals("detalhe_processo") ){
                    //store only corresponding field by creating new FormRowSet
                    newRow.put("n_wf_banco", originalRow.getProperty("n_wf_banco"));
                    newRow.put("complex_processo", originalRow.getProperty("complex_processo"));
                    newRow.put("fase_processo", originalRow.getProperty("fase_processo"));
                    newRow.put("data_entrada", originalRow.getProperty("data_entrada"));
                    newRow.put("forma_outorga", originalRow.getProperty("forma_outorga"));
                    newRow.put("origem_processo", originalRow.getProperty("origem_processo"));
                    newRow.put("estado_contratacao", originalRow.getProperty("estado_contratacao"));
                    newRow.put("n_wf_pl", n_wf_pl);
                    
                }else if(fieldIds[i].equals("banco") ){
                    //store only corresponding field by creating new FormRowSet
                    newRow.put("nome_banco", originalRow.getProperty("nome_banco"));
                    newRow.put("gestor_banco", originalRow.getProperty("gestor_banco"));
                    
                }else if(fieldIds[i].equals("intervenientes") ){
                    String intervenienteId = UuidGenerator.getInstance().getUuid();
                    if(!originalRow.getProperty("nome").isEmpty() || !originalRow.getProperty("nif").isEmpty()){
                        FormRowSet newGridRows = new FormRowSet();
                        FormRow newGridRow = new FormRow();
                        
                        newGridRow.put("id_intervenientes", foreignKey);
                        newGridRow.put("nome", originalRow.getProperty("nome"));
                        newGridRow.put("nif", originalRow.getProperty("nif"));
                        
                        newGridRows.add(newGridRow);
                        storeToOtherFormDataTable(appDef, element, newGridRows, formData, "interveniente", intervenienteId);
                    }
                }
                newRow.setProperty("id_processo", processId);
                newRows.add(newRow);
                storeToOtherFormDataTable(appDef, element, newRows, formData, formIds[i], foreignKey);
            }
            multipagedForms.add(formPrimaryKey);
            //LogUtil.info(appDef.toString(), "passa:" + multipagedForms);
            
            storeToOtherFormDataTable(appDef, element, multipagedForms, formData, "processo", processId);
            
            String queryGetAssignment = "";
            
            var idSLA = UuidGenerator.getInstance().getUuid();
            FormRowSet rowSetSLA = new FormRowSet();
            FormRow rowsSLA = new FormRow();
            rowsSLA.put("id_processo", processId);
            rowsSLA.put("nome_atividade", originalRow.getProperty("fase_processo"));
            rowsSLA.put("data_inicio", originalRow.getProperty("data_entrada"));
            rowsSLA.put("sla", originalRow.getProperty("data_entrada"));
            rowsSLA.put("data_fim", originalRow.getProperty("data_entrada"));
            rowSetSLA.add(rowsSLA);
            storeToOtherFormDataTable(appDef, element, rowSetSLA, formData, "slaAtividade", idSLA);
        }else{
            //Apresentar erro e nao deixar criar o processo
            ////String id = "importaProcesso_nome_banco";
            ////formData.addFormError(id, "Não é possível criar um processo com o Número Workflow Banco = " + nWFBanco + ". O mesmo já está associado a um processo do banco " + bancoValor + ".");
            //LogUtil.info("Beanshell store binder ------", id);
        }
        

    }catch(Exception e){
        LogUtil.error(appDef.toString(), e, "Error creating process");
    }
     
    return rows;
}

public void storeToOtherFormDataTable(AppDefinition appDef, Element element, FormRowSet rows, FormData formData, String formId, String id) {
    try{
        //LogUtil.info(appDef.toString(), "id:" + id);
        AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
  
        FormRowSet storedInfo = appService.storeFormData(appDef.getId(), appDef.getVersion().toString(), formId, rows, id);
        //LogUtil.info(appDef.toString(), "Stored info:" + storedInfo);
    }catch(Exception e){
        LogUtil.error(appDef.toString(), e, "Error storing data");
    }
    
}
  
//call execute method with injected variable
return storeData(element, rows, formData);
