                String id = FormUtil.getElementParameterName(element);
                formData.addFormError(id, "É necessário existir um cliente principal");
                result = false;
            }
        }
    } else {
        //ignore if the field 1 not exist
    }
 
    return result;
}
 
//call validate method with injected variable
