public FormRowSet getOrigemProcesso() {
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    String origemVal = "";
    String origem = "";

    AppDefinition appDef = AppUtil.getCurrentAppDefinition();


    try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();


            if(!con.isClosed()){


                String ProcessID = "#process.processId#";
                String idDetalheProcesso = "#form.detalhe_processo.id[{form.processos.detalhe_processo}]#";


                /**Caso outro utilizador que não o assignado à tarefa faça alterações no processo, não será possivel obter o id do processo**/
                //Assumindo que não existem subprocessos em paralelo
                //Obter o id do processo mais recente
                if(ProcessID.startsWith("#process.processId")){

                    //Obter campos do formulário de detalhe do processo
                    Map paramValues = FormUtil.loadFormData(appDef.getAppId(), appDef.getVersion().toString(), "detalheProcesso", idDetalheProcesso, false, false, false, null);
                    String mainProcessId = paramValues.get("id_processo");
                    String queryGetProcessId = "SELECT Id FROM shkprocesses WHERE ActivityRequesterProcessId = ? order by created desc ;";
                    stmt = con.prepareStatement(queryGetProcessId);
                    stmt.setObject(1, mainProcessId);
                    rs = stmt.executeQuery();
                    if(rs.next()){
                        ProcessID = rs.getObject("Id");
                    }
                }

                String queryGetOrigem = "SELECT c_origem_processo as 'Origem' FROM app_fd_detalhe_processo WHERE id = ?";
                stmt = con.prepareStatement(queryGetOrigem);
                stmt.setObject(1, idDetalheProcesso);
                rs = stmt.executeQuery();
                if(rs.next()){
                    origemVal = rs.getObject("Origem").toString().trim();
                }

                String queryGetOrigemLabel = "SELECT c_label as 'Label' FROM app_fd_listas_de_valores WHERE c_nome_campo = 'origem_processo' AND c_value = ? ;";
                stmt = con.prepareStatement(queryGetOrigemLabel);
                stmt.setObject(1, origemVal);
                rs = stmt.executeQuery();
                if(rs.next()){
                    origem = rs.getString(1);
                }


            }

    }catch(Exception e){
        LogUtil.error(appDef.toString(), e, "Error getting info from DB");
    }finally{
        try{
            if(rs != null){
            rs.close();
            }
            if(stmt != null){
                stmt.close();
            }
            if(con != null && !con.isClosed()) {
                con.close();
            }
        }catch(Exception e){
            LogUtil.error(appDef.toString(), e, "Error closing DB connection");
        }

    }
    FormRowSet f = new FormRowSet();
    FormRow r1 = new FormRow();

    r1.put(FormUtil.PROPERTY_VALUE, origemVal);
    r1.put(FormUtil.PROPERTY_LABEL, origem);
    f.add(r1);
    return f;
}

return getOrigemProcesso();

