FormRowSet f = new FormRowSet();
f.setMultiRow(true);
 
//Get Joget's current datasource configs
DataSource ds = (DataSource) AppUtil.getApplicationContext().getBean("setupDataSource");
 
con = ds.getConnection();
 
try {
    if (!con.isClosed()) {
    
        String dep = "#currentUser.department.id#";
        
        //Here you can query from one or multiple tables using JOIN etc
        String queryGetObs = "SELECT p.id as PID, dp.c_observacoes as DPOBS, i.c_observacoes_intervenientes as IOBS, ab.c_observacoes as ABOBS, ab.c_observacoes_condicionalismo as ABCOBS, al.c_observacoes_pagamento as ALPOBS, a.c_observacoes_agendamento as AOBS FROM app_fd_processos p LEFT JOIN app_fd_detalhe_processo dp ON dp.id = p.c_detalhe_processo LEFT JOIN app_fd_interv_processo i ON i.id = p.c_intervenientes LEFT JOIN app_fd_anal_banc_processo ab ON ab.id = p.c_anal_banc LEFT JOIN app_fd_anal_legal_processo al ON al.id = p.c_anal_legal LEFT JOIN app_fd_agendamento a ON a.id = p.c_agendamento WHERE p.id = '#form.processos.id#'";
        PreparedStatement stmt = con.prepareStatement(queryGetObs);
        //stmt.setString(1, recordId);
      
        //Execute the SELECT SQL statement
        ResultSet rs = stmt.executeQuery();
             
        //Get value from columns of record(s)
        if (rs.next()) {
            String dp_obs = rs.getObject("DPOBS");
            String i_obs = rs.getObject("IOBS");
            String ab_obs =rs.getObject("ABOBS");
            String ab_cond_obs = rs.getObject("ABCOBS");
