                r_intervenientes.put("tabela", "Intervenientes:");
                r_intervenientes.put("observacoes", i_obs);
                f.add(r_intervenientes);
            }
            if(ab_obs != null && !ab_obs.equals("")){
                if( dep.contains("CNT_BIC") ){
                    FormRow r_anal_banc = new FormRow();
                    r_anal_banc.put("tabela", "Análise Bancária:");
                    r_anal_banc.put("observacoes", ab_obs);
                    f.add(r_anal_banc);
                }
            }
            if(ab_cond_obs != null && !ab_cond_obs.equals("")){
                if( dep.contains("CNT_BIC") ){
                    FormRow r_anal_banc_condicionalismo = new FormRow();
                    r_anal_banc_condicionalismo.put("tabela", "Análise Bancária - Condicionalismo:");
                    r_anal_banc_condicionalismo.put("observacoes", ab_cond_obs);
                    f.add(r_anal_banc_condicionalismo);
                }
            }
            if(al_obs != null && !al_obs.equals("")){
                FormRow r_anal_legal = new FormRow();
                r_anal_legal.put("tabela", "Análise Legal - Pagamento:");
                r_anal_legal.put("observacoes", al_obs);
                f.add(r_anal_legal);
            }
            if(a_obs != null && !a_obs.equals("")){
                FormRow r_agendamento = new FormRow();
                r_agendamento.put("tabela", "Agendamento:");
                r_agendamento.put("observacoes", a_obs);
                f.add(r_agendamento);
            }
        }
    }
}catch(Exception ex) {
        LogUtil.error("Your App/Plugin Name", ex, "Error storing using jdbc");
} finally {
