public FormRowSet getUserAssigned() {
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    String gestorPL = "";

    AppDefinition appDef = AppUtil.getCurrentAppDefinition();


    try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();


            if(!con.isClosed()){


                String ProcessID = "#process.processId#";
                String idDetalheProcesso = "#form.detalhe_processo.id[{form.processos.detalhe_processo}]#";

                /**Caso outro utilizador que não o assignado à tarefa faça alterações no processo, não será possivel obter o id do processo**/
                //Assumindo que não existem subprocessos em paralelo
                //Obter o id do processo mais recente
                if(ProcessID.startsWith("#process.processId")){

                    //Obter campos do formulário de detalhe do processo
                    Map paramValues = FormUtil.loadFormData(appDef.getAppId(), appDef.getVersion().toString(), "detalheProcesso", idDetalheProcesso, false, false, false, null);
                    String mainProcessId = paramValues.get("id_processo");
                    String queryGetProcessId = "SELECT Id FROM shkprocesses WHERE ActivityRequesterProcessId = ? order by created desc ;";
                    stmt = con.prepareStatement(queryGetProcessId);
                    stmt.setObject(1, mainProcessId);
                    rs = stmt.executeQuery();
                    if(rs.next()){
                        ProcessID = rs.getObject("Id");
                    }

                }

                //Obter o utilizador a quem está atribuida a tarefa
                String userAssigned = "";
                String queryGetSubfase = "SELECT asst.ResourceId as 'resourceId' FROM shkassignmentstable asst WHERE asst.activityprocessid =? ;";
                stmt = con.prepareStatement(queryGetSubfase);
                stmt.setObject(1, ProcessID);
                rs = stmt.executeQuery();
                if(rs.next()){
                    userAssigned = rs.getObject("resourceId").toString();
                }



                String idDetalhe_processo = "#form.processos.detalhe_processo#";
                //Verificar o utilizador atribuido e atualizar caso esteja errado
                String queryGetGestorPL="SELECT c_gestor_pl FROM app_fd_detalhe_processo WHERE id = ? ;";
                stmt = con.prepareStatement(queryGetGestorPL);
                stmt.setObject(1, idDetalhe_processo);
                rs = stmt.executeQuery();

                if(rs.next()){
                    gestorPL = rs.getObject("c_gestor_pl");
                }



                if(gestorPL == null || gestorPL.isEmpty() || !gestorPL.equals(userAssigned)){
                    String queryUpdateGestorPL = "UPDATE app_fd_detalhe_processo SET c_gestor_pl = ? WHERE id = ?";
                    stmt = con.prepareStatement(queryUpdateGestorPL);
                    stmt.setObject(1, userAssigned);
                    stmt.setObject(2, idDetalhe_processo); //id registo do detalhe_processo
                    rs = stmt.executeQuery();
                    gestorPL=userAssigned;
                }




            }

    }catch(Exception e){
        LogUtil.error(appDef.toString(), e, "Error getting info from DB");
    }finally{
        try{
            if(rs != null){
            rs.close();
            }
            if(stmt != null){
                stmt.close();
            }
            if(con != null && !con.isClosed()) {
                con.close();
            }
        }catch(Exception e){
            LogUtil.error(appDef.toString(), e, "Error closing DB connection");
        }

    }
    FormRowSet f = new FormRowSet();
    FormRow r1 = new FormRow();
    r1.put(FormUtil.PROPERTY_VALUE, gestorPL);
    r1.put(FormUtil.PROPERTY_LABEL, gestorPL);
    f.add(r1);
    return f;
}

return getUserAssigned();

