public FormRowSet load(Element element, String primaryKey, FormData formData) {
    
    FormRowSet rows = new FormRowSet();
    
    String gestor = "#form.docs_processo.gestor#";
    
    if (gestor == null || gestor.equals("") || gestor.contains("form.docs_processo.gestor")) {
        String cUser = "#currentUser.username#";
        String cUserName = "#currentUser.fullName#";
        
        FormRow option = new FormRow();
        option.setProperty(FormUtil.PROPERTY_VALUE, cUser);
        option.setProperty(FormUtil.PROPERTY_LABEL, cUserName);
        rows.add(option);
    }else{
        String gestorName = "#user." + gestor + ".fullName#";
        gestorName = AppUtil.processHashVariable(gestorName, null, null, null, null);
        
        FormRow option = new FormRow();
        option.setProperty(FormUtil.PROPERTY_VALUE, gestor);
        option.setProperty(FormUtil.PROPERTY_LABEL, gestorName);
        rows.add(option);
    }
        
    return rows;
}
 
//call load method with injected variable
return load(element, primaryKey, formData);

