                            nomeAtividade= rs.getObject("name").toString();
                        }
                        
                        stmt = con.prepareStatement(queryGetUserAssign);
                        stmt.setObject(1, ProcessID);
                        rs = stmt.executeQuery();
                        
                        if(rs.next()){
                            utilizador= rs.getObject("resourceid").toString();
                        }
                        
                        
                        
                       
                        //Criar SLA Pendente
                        String queryCreateSLA = "INSERT INTO app_fd_sla_atividade (id, dateCreated, dateModified, createdBy,createdByName ,modifiedBy, modifiedByName, c_data_inicio, c_data_fim, c_nome_atividade, c_id_processo, c_utilizador_atribuido, c_estado_atividade) VALUES (?,?,?,?,?,?,?,?,?,?,?,?,?);";
                        stmt = con.prepareStatement(queryCreateSLA);
                        
                        var idSLA = UuidGenerator.getInstance().getUuid();
                        var dateNow ="#date.yyyy-MM-dd HH:mm#";
                        var currentUsername = "#currentUser.username#";
                        var currentNameUser = "#currentUser.firstName#" + "#currentUser.lastName#";
                        String dataFim = calculateSLAD(dateNow, holidays, sla, Urgencia);
                       
                        var estadoAtividade = "Pendente";
                        stmt.setObject(1, idSLA);
                        stmt.setObject(2, dateNow);
                        stmt.setObject(3, dateNow);
                        stmt.setObject(4, currentUsername);
                        stmt.setObject(5, currentNameUser);
                        stmt.setObject(6, currentUsername);
                        stmt.setObject(7, currentNameUser);
                        stmt.setObject(8, dateNow);
                        stmt.setObject(9, dataFim);   
                        stmt.setObject(10, nomeAtividade);
                        stmt.setObject(11, ProcessID);
                        stmt.setObject(12, utilizador);
                        stmt.setObject(13, estadoAtividade);
            
                        rs = stmt.executeQuery();
                   }
                   
                   
                   //Caso o estado de contratação esteja a pendente
               }else{
                   
                   //Se existir algum SLA pendente
                   if(SLAOnHold){
                       //Atualizar o sla de Pendente para Em curso
                        String queryReset= "UPDATE app_fd_sla_atividade SET c_estado_atividade = 'Em curso' WHERE c_id_processo=? AND c_estado_atividade = 'Pendente';"; 
                        stmt = con.prepareStatement(queryReset);
                        stmt.setObject(1, ProcessID);
                        rs = stmt.executeQuery();
                   }
                   
                   
                    //Obter utilizador atribuido e data final
                    String dataFinalAtribuida = "";
                    String dataInicioAtribuida = "";
                    String queryGetOnHoldSLA = "SELECT c_utilizador_atribuido, c_data_fim, c_data_inicio FROM app_fd_sla_atividade WHERE c_id_processo=? AND (c_estado_atividade = 'Em curso' OR c_estado_atividade = 'Pendente');";
                    stmt = con.prepareStatement(queryGetOnHoldSLA);
                    stmt.setObject(1, ProcessID);
                    rs = stmt.executeQuery();
                    //Ver se existe utilizador atribuido no SLA 
                    if(rs.next()){
                        String utilizadorAtribuido = rs.getObject("c_utilizador_atribuido").toString();
                        dataFinalAtribuida = rs.getObject("c_data_fim").toString();
                        dataInicioAtribuida = rs.getObject("c_data_inicio").toString();
                        
                        String estadoAtividade = "";
                        if(SLAIsOpen){
                            estadoAtividade = "Em curso";
                        }else{
                            estadoAtividade = "Pendente";
                        }
                        if(utilizadorAtribuido.trim().isEmpty()){

                            //Atualizar utilizador atribuido
                            String queryReset= "UPDATE app_fd_sla_atividade SET c_utilizador_atribuido =? WHERE c_id_processo=? AND c_estado_atividade =?;"; 
                            
                            stmt = con.prepareStatement(queryReset);
                            stmt.setObject(1, utilizador);
                            stmt.setObject(2, ProcessID);
                            stmt.setObject(3, estadoAtividade);
                            rs = stmt.executeQuery();
                            
                        }
                     
                        //Atualizar nova data final de SLA 
                        String dataFimNova = calculateSLAD(dataInicioAtribuida, holidays, sla, Urgencia);
                       
                        if(!dataFimNova.equals(dataFinalAtribuida)){
                            String queryUpdateSLA= "UPDATE app_fd_sla_atividade SET c_data_fim =? WHERE c_id_processo=? AND c_estado_atividade =?;"; 
                            stmt = con.prepareStatement(queryUpdateSLA);
                            stmt.setObject(1, dataFimNova);
                            stmt.setObject(2, ProcessID);
                            stmt.setObject(3, estadoAtividade);
                            rs = stmt.executeQuery();
                        }
                        
                    }
               }
               
               
               if(!utilizador.equals(modifiedByUser)){
                    String queryUpdateFiltroValidacao = "UPDATE app_fd_detalhe_processo SET c_validar_filtro = 'NAO' WHERE id = ? ;";
                    stmt = con.prepareStatement(queryUpdateFiltroValidacao);
                    stmt.setObject(1, idDetalheProcesso);
                    rs = stmt.executeQuery();
               }
               
               
               //Atualizar SubFase do Processo
                String currentSubfase = "#form.detalhe_processo.subfase_processo[{form.processos.detalhe_processo}]#";
                String actualSubfase = "";
                String userAssigned = "";
                String idDetalhe_processo = "#form.processos.detalhe_processo#";
                String queryGetSubfase = "SELECT  a.`name` as subfase, asst.ResourceId as 'resourceId' FROM shkassignmentstable asst JOIN shkactivities a ON a.id=asst.activityid WHERE asst.activityprocessid =? ;";
                stmt = con.prepareStatement(queryGetSubfase);
                stmt.setObject(1, ProcessID);
                rs = stmt.executeQuery();
                
                
                if(rs.next()){
                    actualSubfase = rs.getObject("subfase").toString();
                    userAssigned = rs.getObject("resourceId").toString();
                    if(currentSubfase.trim().isEmpty() || currentSubfase.equals("") || !currentSubfase.equals(actualSubfase) ){
                        String queryUpdateSubfase = "Update app_fd_detalhe_processo SET c_subfase_processo = ? WHERE id =? ;";
                        stmt = con.prepareStatement(queryUpdateSubfase);
                        stmt.setObject(1, actualSubfase);
                        stmt.setObject(2, idDetalhe_processo); //id registo do detalhe_processo
                        rs = stmt.executeQuery();
                    }
                }
                
                //Atualizar Gestor PL
                String queryGetGestorPL="SELECT c_gestor_pl FROM app_fd_detalhe_processo WHERE id = ? ;";
                String gestorPL = "";
                stmt = con.prepareStatement(queryGetGestorPL);
                stmt.setObject(1, idDetalhe_processo);
                rs = stmt.executeQuery();
                
                if(rs.next()){
                    gestorPL = rs.getObject("c_gestor_pl").toString();
                }
                
                if(gestorPL == null || gestorPL.isEmpty() || !gestorPL.equals(userAssigned)){
                    String queryUpdateGestorPL = "UPDATE app_fd_detalhe_processo SET c_gestor_pl = ? WHERE id = ?";
                    stmt = con.prepareStatement(queryUpdateGestorPL);
                    stmt.setObject(1, userAssigned);
                    stmt.setObject(2, idDetalhe_processo); //id registo do detalhe_processo
                    rs = stmt.executeQuery();
                }
                
                
                
                
               
            }
            
        }catch(Exception e){
            LogUtil.error(appDef.toString(), e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error(appDef.toString(), e, "Error closing DB connection");
            }
            
        }
        

    //Caso o processo não esteja bloqueado
    if(!isLocked){
        
        if(rows != null && !rows.isEmpty()) {
            PluginManager pluginManager = (PluginManager) AppUtil.getApplicationContext().getBean("pluginManager");
            FormStoreBinder binder = (FormStoreBinder) pluginManager.getPlugin("org.joget.apps.form.lib.WorkflowFormBinder");
            binder.store(element, rows, formData);
            
        }
        
    }else{
        formData.addFormError("record_lock", "Campo obrigatório");
    }
    
    return rows;
}
