import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.service.FormUtil;
import org.joget.commons.util.LogUtil;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;
import org.joget.apps.app.service.AppUtil;

public FormRowSet getData() {
    FormRowSet results = new FormRowSet();
    results.setMultiRow(true);
    Connection con = null;
    try {
        // retrieve connection from the default datasource
        DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
        con = ds.getConnection();

	    if(!con.isClosed()){
	        String[] ids = "#requestParam.fk_instance_ids#".split(";"); 

	        if (ids.length > 0) {
	            String sql = "SELECT aa.name AS appName, p.Id AS processId, p.Name AS processName, p.ResourceRequesterId AS requester, "; 
	            sql += "FROM_UNIXTIME(SUBSTRING(CAST(p.Started AS CHAR), 1, 10), '%Y-%m-%d %H:%i:%s') AS startTime ";
	            sql += "FROM SHKProcesses p ";
	            sql += "JOIN SHKProcessDefinitions pd ON pd.Name = p.PDefName ";
	            sql += "LEFT JOIN app_package ap ON pd.PackageId = ap.packageId ";
	            sql += "LEFT JOIN app_app aa ON aa.appId = ap.appId "; 
	            sql += "WHERE p.Id IN (?";
                for (int i = 1; i < ids.length; i++) {
                    LogUtil.info("ids", "AAAAAAAa" + ids[i]);
                    sql += ",?";
                }
                sql += ") ";

                PreparedStatement stmt = con.prepareStatement(sql);
                for (int i = 1; i <= ids.length; i++) {
                    stmt.setString(i, ids[i-1]);
                }

		        ResultSet rs = stmt.executeQuery();
		        while (rs.next()) {
		            FormRow row = new FormRow();
		            row.setId(rs.getString(2));
		            row.put("appName", (rs.getString(1) != null)?rs.getString(1):"");
		            row.put("processId", (rs.getString(2) != null)?rs.getString(2):"");
		            row.put("processName", (rs.getString(3) != null)?rs.getString(3):"");
		            row.put("requester", (rs.getString(4) != null)?rs.getString(4):"");
		            row.put("startTime", (rs.getString(5) != null)?rs.getString(5):"");
		            results.add(row);
		        }
		    }
        }
	} catch(Exception ex) {
	    LogUtil.error("Exception", ex, "");
	} finally {
	    try {
            if(con != null)
                con.close();
        } catch(SQLException e) {}
	}    
    return results;
}
return getData();

import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.service.FormUtil;
import org.joget.commons.util.LogUtil;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.app.service.AppService;
import org.joget.apps.app.model.AppDefinition;
import org.joget.workflow.model.WorkflowProcess;

public FormRowSet getData() {
    FormRowSet results = new FormRowSet();

    FormRow erow = new FormRow();
	erow.put(FormUtil.PROPERTY_VALUE, "");
	erow.put(FormUtil.PROPERTY_LABEL, "");
	results.add(erow);

    AppDefinition orgAppDef = AppUtil.getCurrentAppDefinition();
    try {
        AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
        Map appProcessMap = appService.getPublishedProcesses(null);

		if (appProcessMap != null) {
		    for (AppDefinition app: appProcessMap.keySet()) {
		        for (WorkflowProcess p : appProcessMap.get(app)) {
		            FormRow row = new FormRow();
		            row.put(FormUtil.PROPERTY_VALUE, p.getId());
		            row.put(FormUtil.PROPERTY_LABEL, app.getName() + " - " + p.getName());
		            results.add(row);
		        }
		    }
		}
	} catch(Exception ex) {
	    LogUtil.error("Exception", ex, "");
	} finally {
	    AppUtil.setCurrentAppDefinition(orgAppDef);
	}  
    return results;
}
return getData();

import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.form.model.FormRow;
import org.joget.workflow.model.service.WorkflowManager;
import org.joget.workflow.model.WorkflowProcess;
import org.joget.apps.app.service.AppUtil;
import org.joget.workflow.model.WorkflowProcessLink;

public FormRowSet storeData() {
    FormRow row = rows.get(0);

    String instanceId = "#requestParam.id#";
    String ids = row.get("instance_ids");
    String processDefId = row.get("process");

    String[] idList = ids.split(";");
    for (String id : idList) {
        String recordId = id;
        WorkflowManager wm = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");

        WorkflowProcessLink link = wm.getWorkflowProcessLink(id);
        if (link != null) {
            recordId = link.getOriginProcessId();
        }

        WorkflowProcess process = wm.getRunningProcessById(id);
        if (process != null) {
            String tempProcessDefId = processDefId;
            if (tempProcessDefId.isEmpty()) {
                tempProcessDefId = process.getId().replaceAll("#.+#", "#latest#");
            }
            wm.processStart(tempProcessDefId, null, null, process.getRequesterId(), recordId, false);
        }
    }
    return rows;
}

return storeData();
