import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.model.Element;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.form.model.FormStoreBinder;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;
import org.joget.apps.form.service.FormUtil;
import org.joget.apps.app.model.AppDefinition;
import org.joget.commons.util.LogUtil;

 
public FormRowSet load(String[] values) {
    
    FormRowSet rows = new FormRowSet();
    
    FormRow option = new FormRow();
    option.setProperty(FormUtil.PROPERTY_VALUE, "");
    option.setProperty(FormUtil.PROPERTY_LABEL, "");
    rows.add(option);
    
    String servico = null;
    if (values != null && values.length > 0) {
        servico = "%" + values[0] + "%";
    
        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
        String outorga_value = "";
        String outorga_label = "";
        
        AppDefinition appDef = AppUtil.getCurrentAppDefinition();
        
        try{
                DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                con = ds.getConnection();
                
                
                if(!con.isClosed()){
                    String idDetalhe_processo = "#form.processos.detalhe_processo#";
                    //Verificar o utilizador atribuido e atualizar caso esteja errado
                    String query="SELECT c_value, c_label FROM app_fd_listas_de_valores WHERE c_nome_campo = ? AND c_servico LIKE ?;";
                    stmt = con.prepareStatement(query);
                    stmt.setObject(1, "operativa");
                    stmt.setObject(2, servico);
                    rs = stmt.executeQuery();
                    
                    while(rs.next()){
                        outorga_value = rs.getString(1);
                        outorga_label = rs.getString(2);
                        
                        FormRow option = new FormRow();
                        option.setProperty(FormUtil.PROPERTY_VALUE, outorga_value);
                        option.setProperty(FormUtil.PROPERTY_LABEL, outorga_label);
                        rows.add(option);
                        
                    }
                }
                
        }catch(Exception e){
            LogUtil.error("Importa processo - forma de outorga", e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error("Importa processo - forma de outorga", e, "Error closing DB connection");
            }
            
        }
        
    }

    return rows;
}
 
//call load method with injected variable
return load(values);
