import org.joget.workflow.model.service.*;
import org.joget.commons.util.LogUtil;
import org.joget.apps.app.service.AppUtil;
 
LogUtil.info("BeanShell", "Running post processing tool");
String ProcessID = workflowAssignment.getProcessId();
String Username = "#currentUser.username#";
String DepartmentID = "#currentUser.department.id#";
String ActivityID = "#assignment.activityDefId#";
String Performer = null;


LogUtil.info("BeanShell", "Process ID: " + ProcessID);
LogUtil.info("BeanShell", "Username: " + Username);
LogUtil.info("BeanShell", "Departamento: " + DepartmentID);
LogUtil.info("BeanShell", "Atividade: " + ActivityID);

Performer = "#process.activityInst." + ActivityID + ".performer#";
LogUtil.info("BeanShell", "Parformer String: " + Performer);
Performer = AppUtil.processHashVariable(Performer, null, null, null);
LogUtil.info("BeanShell", "Parformer: " + Performer);

Performer = "#performer." + ActivityID + ".id#";
LogUtil.info("BeanShell", "2 Parformer String: " + Performer);
Performer = AppUtil.processHashVariable(Performer, null, null, null);
LogUtil.info("BeanShell", "2 Parformer: " + Performer);

WorkflowManager wm = (WorkflowManager) pluginManager.getBean("workflowManager");
LogUtil.info("BeanShell", "Running post processing tool 2");


wm.processVariable(ProcessID, DepartmentID , Username);

LogUtil.info("BeanShell", "Participant21: " + AppUtil.processHashVariable("#variable.TestDepartmentAssig#", null, null, null));
LogUtil.info("BeanShell", "Participant21: " + AppUtil.processHashVariable("#variable.TestDepartmentAssig2#", null, null, null));
