//this function will store rows data to external source using JDBC
public void storeUsingJDBC(FormRowSet rows, String id) {
    Connection con = null;
    try {
        // retrieve connection from the default datasource
        DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
        con = ds.getConnection();
  
        if (!con.isClosed()) {
            //manually handle insert and update by checking the data exist or not
            String selectQuery = "SELECT id FROM app_fd_processos WHERE id=?";
            PreparedStatement stmt = con.prepareStatement(selectQuery);
            stmt.setString(1, id);
            ResultSet rs = stmt.executeQuery();
  
            Boolean isExist = false;
            if (rs.next()) {
                isExist = true;
            }
  
            FormRow row = rows.get(0);
  
            if (isExist) {
                String updateQuery = "UPDATE app_fd_processos SET n_wf_banco = ?, nome_banco = ?, complex_processo = ? WHERE id = ?";
                PreparedStatement ustmt = con.prepareStatement(updateQuery);
                ustmt.setString(1, row.getProperty("n_wf_banco"));
                ustmt.setString(2, row.getProperty("nome_banco"));
                ustmt.setString(3, row.getProperty("complex_processo"));
                ustmt.setString(4, id);
                ustmt.executeUpdate();
            } else {
                String insertQuery = "INSERT INTO app_fd_processos (id, n_wf_banco, nome_banco, complex_processo) values (?, ?, ?, ?)";
                PreparedStatement istmt = con.prepareStatement(insertQuery);
                istmt.setString(1, id);
                istmt.setString(2, row.getProperty("n_wf_banco"));
                istmt.setString(3, row.getProperty("nome_banco"));
                istmt.setString(4, row.getProperty("complex_processo"));
                istmt.executeUpdate();
            }
        }
    } catch (Exception e) {
        LogUtil.error("Sample app - StoreToMultipleSource form", e, "Error storing using jdbc");
    } finally {
        try {
            if(con != null) {
                con.close();
            }
        } catch (SQLException e) {}
    }
}

