import org.joget.workflow.model.service.WorkflowManager;
import org.joget.apps.app.service.AppUtil;
import org.joget.workflow.model.service.WorkflowUserManager;
import org.joget.apps.app.model.AppDefinition;
import javax.servlet.http.HttpServletRequest;
import org.joget.commons.util.LogUtil;
import org.joget.apps.datalist.model.DataListBinderDefaultimport;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;


String idProcesso="";
String aid = "";
String resourceId = "";
for (String key : rowKeys) {
    idProcesso = key;
    
}
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                String queryGetAid = "SELECT a.id as 'Aid', sass.ResourceId as 'ResourceId' FROM shkprocesses p LEFT JOIN shkactivities a ON p.id = ProcessId LEFT JOIN shkassignmentstable sass ON sass.ActivityId = a.id WHERE p.ActivityRequesterProcessId = ?";
                stmt = con.prepareStatement(queryGetAid);
                stmt.setObject(1, idProcesso);
                rs = stmt.executeQuery();
                if(rs.next()){
                    aid = rs.getObject("Aid");
                    resourceId= rs.getObject("ResourceId");
                    
                    LogUtil.info("Process", "AAAAA" + idProcesso);
                    LogUtil.info("ActivityId", "AAAAA" + aid);
                    LogUtil.info("UTILIZADOR", "BBBB" + resourceId);
                    
                    
                    WorkflowManager wm = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
                    WorkflowUserManager wum = (WorkflowUserManager) AppUtil.getApplicationContext().getBean("workflowUserManager");
                    
                    //get the values from the 
                    String replaceUser = resourceId;
                    String username = "#currentUser.username#";
                
                
                    //getCurrentThreadUser() Method used by system to get current thread user
                    String currentUser = wum.getCurrentThreadUser();
                    wum.setCurrentThreadUser(replaceUser);
                    wum.setCurrentThreadUser(currentUser);
                    
                    //Reassigns the assignment from a user (replaceUser) to another user (username)
                    wm.assignmentReassign("", "", aid, username, replaceUser);
                    
                }
                
                
            }
            
    }catch(Exception e){
            LogUtil.error(appDef.toString(), e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error(appDef.toString(), e, "Error closing DB connection");
            }
            
        }
