import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.model.Element;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.commons.util.LogUtil;
import org.joget.apps.form.model.Form;
import org.joget.apps.form.model.defaultHashVariablePlugin;
import org.joget.apps.form.service.FormUtil;
import java.util.Collection;
import org.joget.apps.form.dao.FormDataDao;
import org.joget.apps.app.model.AppDefinition;
import org.joget.apps.app.model.FormDefinition;
import org.joget.apps.app.dao.FormDefinitionDao;
import org.joget.apps.form.service.FormService;

public FormRowSet load(String[] values) {
    
    FormRowSet rows = new FormRowSet();
    Connection con = null;
    
    //set value of formulario based on dependency value
    String formulario = "";
    
    
    if (values != null && values.length > 0) {
        formulario = values[0];
    }
    

    if(formulario.isEmpty()){
        FormRow option = new FormRow();
        option.setProperty(FormUtil.PROPERTY_VALUE, "");
        option.setProperty(FormUtil.PROPERTY_LABEL, "");
        rows.add(option);
    }else{
    
    FormDataDao formDataDao = (FormDataDao) AppUtil.getApplicationContext().getBean("formDataDao");
    FormDefinitionDao formDefinitionDao = (FormDefinitionDao) AppUtil.getApplicationContext().getBean("formDefinitionDao");
    FormService formService = (FormService) AppUtil.getApplicationContext().getBean("formService");
    
    
    
    AppDefinition appDef = AppUtil.getCurrentAppDefinition();
    FormDefinition formDef = formDefinitionDao.loadById(formulario, appDef);
    
    HashSet map = formDataDao.getFormDefinitionColumnNames(formDef.getTableName());
    map.add("dateCreated");
    map.add("dateModified");
    if(formDef != null){
        String formJson = formDef.getJson();
        if(formJson != null) {
            Form form = (Form) formService.createElementFromJson(formJson, false);

            for(String field : map){
                
                //Condição de excessão para hiddenField de adicionar ficheiros
                if(formulario.equals("documentos") && field.equals("docs_processo")){
                    FormRow option = new FormRow();
                    option.setProperty(FormUtil.PROPERTY_VALUE, field);
                    option.setProperty(FormUtil.PROPERTY_LABEL, "Adicionar Ficheiros");
                    rows.add(option);
                }
                    
                String label = "";
                Element element = FormUtil.findElement(field, form, null, true);
                if(element != null){
                    label = element.getPropertyString(FormUtil.PROPERTY_LABEL);
                }
                if(label != null  && !label.isEmpty() && label != ""){
                    FormRow option = new FormRow();
                    option.setProperty(FormUtil.PROPERTY_VALUE, field);
                    option.setProperty(FormUtil.PROPERTY_LABEL, label);
                    rows.add(option);
                }
            }
        
        }
    }

    }
    

    return rows;
}
 
//call load method with injected variable
return load(values);
