}

DataListService dataListService = (DataListService) AppUtil.getApplicationContext().getBean("dataListService");
String dataInicio = dataListService.evaluateColumnValueFromRow(row, "dataInicio");
DateTimeFormatter formatter = DateTimeFormatter.ofPattern("dd/MM/yyyy HH:mm");
DateTimeFormatter dateFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd");

String result = "";
if(dataInicio !=  null && dataInicio.trim() != "" && value !=  null && !value.isEmpty()){
    ////////LogUtil.info("SLA %", value);
    //if dataInicio date format is not "dd/MM/yyyy HH:mm"
    if(dataInicio.charAt(2) != '/'){
        //if dataInicio date format is of the format "yyyy-MM-dd HH:mm"
        if(dataInicio.charAt(4) == '-' && dataInicio.charAt(10) == ' ' && dataInicio.charAt(13) == ':'){
            dataInicio =  dataInicio.substring(8,10) + "/" + dataInicio.substring(5,7) + "/" + dataInicio.substring(0,4) + " " + dataInicio.substring(11,16);
        }
    }
    //if value date format is not "dd/MM/yyyy HH:mm"
    if(value.charAt(2) != '/'){
        //if value date format is of the format "yyyy-MM-dd HH:mm"
        if(value.charAt(4) == '-' && value.charAt(10) == ' ' && value.charAt(13) == ':'){
            value =  value.substring(8,10) + "/" + value.substring(5,7) + "/" + value.substring(0,4) + " " + value.substring(11,16);
        }
    }

    LocalDateTime dateInit = LocalDateTime.parse(dataInicio, formatter);
    LocalDateTime dateEnd = LocalDateTime.parse(value, formatter);
    LocalDateTime dateNow = LocalDateTime.parse("#date.dd/MM/yyyy HH:mm#",formatter);

    double totalMinutes = ChronoUnit.MINUTES.between(dateInit, dateEnd);
    double passedMinutes = ChronoUnit.MINUTES.between(dateInit, dateNow);

    //Calcular dias não uteis entre a data de inicio e final
    long totalDaysBetween = ChronoUnit.DAYS.between(dateInit, dateEnd);
    long nonWorkDaysTotal = 0;
    LocalDateTime dateInit2 = dateInit; //copy of dateInit
    while(totalDaysBetween>=0){

        if(holidays.contains(dateInit2.format(dateFormatter))){
            nonWorkDaysTotal+=1;
        }else{

            String day = dateInit2.getDayOfWeek().toString();

            switch(day) {
                case "SATURDAY":{
                    nonWorkDaysTotal+=1;
                    break;
                }
                case "SUNDAY":{
                    nonWorkDaysTotal+=1;
                    break;
                }
            }
        }

        dateInit2 = dateInit2.plusDays(1);
        totalDaysBetween-=1;
    }
    totalMinutes = totalMinutes - (1440 * nonWorkDaysTotal); //total de minutos menos dias não uteis em minutos


    //Calcular dias não uteis entre a data de inicio e atual
    long actualDaysBetween = ChronoUnit.DAYS.between(dateInit, dateNow);
    long nonWorkDaysActual = 0;
    while(actualDaysBetween>0){
        if(holidays.contains(dateInit.format(dateFormatter))){
            nonWorkDaysActual+=1;
        }else{
            String day = dateInit.getDayOfWeek().toString();

            switch(day) {
                case "SATURDAY":{
                    nonWorkDaysActual+=1;
                    break;
                }
                case "SUNDAY":{
                    nonWorkDaysActual+=1;
                    break;
                }
            }
        }
        dateInit = dateInit.plusDays(1);
        actualDaysBetween-=1;
    }
    passedMinutes = passedMinutes - (1440 * nonWorkDaysActual); //total de minutos menos dias não uteis em minutos

    int percentage = (Integer)Math.round((passedMinutes * 100)/totalMinutes);

    result = percentage + "%";
}else {
    result = "-";
}


return result;

