import org.joget.apps.datalist.service.DataListService;
import org.joget.apps.datalist.model.DataList;
import org.joget.apps.datalist.model.DataListColumn;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.app.model.FormDefinition;
import org.joget.commons.util.LogUtil;
import org.joget.workflow.util.WorkflowUtil;


public String format(DataList dataList, DataListColumn column, Object row, Object value) {
        String result = (String) value;
        
        LogUtil.info("Beanshell Datalist", "datalist: " + datalist.toString());
        LogUtil.info("Beanshell Datalist", "column: " + column.toString());
        LogUtil.info("Beanshell Datalist", "row: " + row.toString());
        LogUtil.info("Beanshell Datalist", "value: " + value.toString());
  
        if (result != null && !result.isEmpty()) {
            try {
                String formDefId = "documento";
                //FormDefinition formDef = formDefinitionDao.loadById(formId, appDef);
                //String formDefId = getPropertyString("formDefId");
                AppDefinition appDef = AppUtil.getCurrentAppDefinition();
                result = "";
  
                String attachment = "?attachment=true";
                /*if ("true".equals(getPropertyString("attachment"))) {
                    attachment = "?attachment=true";
                }*/
  
                //get the id of this record
                String primaryKeyValue = (String) dataListService.evaluateColumnValueFromRow(row, "c_doc_id");
                 
                HttpServletRequest request = WorkflowUtil.getHttpServletRequest();
  
                //suport for multi values
                for (String v : value.toString().split(";")) {
                    if (!v.isEmpty()) {
                        // determine actual path for the file uploads
                        String fileName = v;
                        String encodedFileName = fileName;
  
                        try {
                            encodedFileName = URLEncoder.encode(fileName, "UTF8").replaceAll("\\+", "%20");
                        } catch (UnsupportedEncodingException ex) {
                            // ignore
                        }
  
                        String filePath = request.getContextPath() + "/web/client/app/" + appDef.getAppId() + "/" + appDef.getVersion().toString() + "/form/download/" + formDefId + "/" + primaryKeyValue + "/" + encodedFileName + "." + attachment;
  
                        if (!result.isEmpty()) {
                            result += ", ";
                        }
                        result += "<a href=\""+filePath+"\" target=\"_blank\">"+StringUtil.stripAllHtmlTag(fileName)+"</a>";
                    }
                }
            } catch (Exception e) {
                LogUtil.error(getClassName(), e, "");
            }
        }
        //String result = "<a href='http://192.168.0.104:8080/jw/web/client/app/workflowPL/1/form/download/documento/de6307ba-cf8d-4dbe-8797-87e6f8bf2c11/Joget_Best-Practices.docx.?attachment=true'>Joget</a>";
        return result;
}

return format(datalist, column, row, value);
