Connection con = null;
PreparedStatement stmt = null;
ResultSet rs = null;
String entidade = "";

if (!value.isEmpty()) {
    try {
        DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
        con = ds.getConnection();

        if(!con.isClosed()){

            //Obter Nome da Entidade
            String queryGetEntityName = "SELECT c_nome_completo FROM app_fd_info_colaboradores WHERE c_cedula_profissional = ?;";
            stmt = con.prepareStatement(queryGetEntityName);
            stmt.setObject(1, value);
            rs = stmt.executeQuery();

            if(rs.next()) {
                entidade = rs.getObject("c_nome_completo");
            }
        }

    } catch(Exception e){
        LogUtil.error("Olimpo | Consultar Processos - Erro ao obter nome de entidade da DB", e, "Error getting info from DB");
    } finally {
        try {
            if(rs != null) {
                rs.close();
            }
            if(stmt != null) {
                stmt.close();
            }
            if(con != null && !con.isClosed()) {
                con.close();
            }
        } catch(Exception e) {
            LogUtil.error("Olimpo | Consultar Processos - Erro ao fechar conexão", e, "Error closing DB connection");
        }
    }    
}


return entidade;

import org.joget.workflow.model.service.WorkflowManager;
