function buildInboxData(data, inboxName){
    var footer = $('.' + inboxName +'-inbox-notification > ul .dropdown-menu-sub-footer').parent();
    var link = $('.' + inboxName +'-inbox-notification > ul .dropdown-menu-sub-footer').attr("href");
            $.each(data.data, function(i, d) {
                var html = "<li class=\"task\"><a href=\"" + link + "?_mode=assignment&activityId=" + d.activityId + "\">";
                html += "<span class=\"header\">" + d.activityName + "</span>";
                html += "<span class=\"message\">" + d.processName + "</span><span class=\"time\">" + formatarData(d.dateCreated)+ "</span>";
                html += "</a></li>";
                footer.before($(html));
            });

}


function formatarData(data){
    var date = new Date(data);

    var day= date.getDate();
    var month= date.getMonth() + 1;
    var year= date.getFullYear();

    var hours = date.getHours();
    var minutes = date.getMinutes();
    var seconds = date.getSeconds();

    var formatedDays = ('0' + day).slice(-2) + '/'+ ('0' + (month)).slice(-2) + '/'+ year;
    var formatedTime = ('0' + hours).slice(-2) + ':' + ('0' + minutes).slice(-2) + ':' + ('0' + seconds).slice(-2);

    var formatedDate = formatedDays + " " + formatedTime;
    return formatedDate;
}
